CREATE TABLE IF NOT EXISTS `hanoi` (
  `ma` int(11) NOT NULL AUTO_INCREMENT,
  `ten` varchar(100) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `gioithieu` varchar(5000) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `anh` varchar(50) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `soluoc` varchar(2000) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  PRIMARY KEY (`ma`)
) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=8 ;

--
-- Dumping data for table `hanoi`
--

INSERT INTO `hanoi` (`ma`, `ten`, `gioithieu`, `anh`, `soluoc`) VALUES
(1, 'Truyền thông và văn hóa', 'Hà Nội thường được xem như nơi tập trung những tinh hoa văn hóa. Trong hàng ngàn năm, vị trí kinh đô khiến thành phố này trở thành nơi quy tụ của những nhân vật ưu tú, những thương nhân, những nghệ nhân, những thợ thủ công lành nghề. Họ tới đây lập nghiệp, mang theo những phong tục, tập quán địa phương và Hà Nội trở thành mảnh đất tiêu biểu cho nền văn hóa của cả Việt Nam. Những danh nhân, nhân vật nổi tiếng của Việt Nam phần đông xuất thân từ những vùng đất khác, nhưng kinh đô Thăng Long thường là nơi họ xây dựng nên sự nghiệp.          Thăng Long - Hà Nội là nơi giao thoa của những nền văn hóa lớn. Hà Nội ít nhiều mang những dấu ấn của văn hóa Trung Hoa, văn minh phương Tây để rồi xây dựng nên những cơ sở đầu tiên của nền nghệ thuật Việt Nam hiện đại với tân nhạc, thơ mới, hội họa, văn học hiện đại, điện ảnh, nhiếp ảnh.          Những nét văn hóa thường được thể hiện qua ngôn ngữ, trang phục, truyền thống ẩm thực, những thú vui giải trí... Người Hà Nội vẫn giữ lại những thú vui tao nhã như chơi hoa, trồng cây cảnh, nuôi chim.... Trang phục của người Hà Nội, dẫu thay đổi nhiều theo thời gian, vẫn được xem là trang nhã và duyên dáng.', '1.jpg', 'Thăng Long - Hà Nội là nơi giao thoa của những nền văn hóa lớn. Hà Nội ít nhiều mang những dấu ấn của văn hóa Trung Hoa, văn minh phương Tây để rồi xây dựng nên những cơ sở đầu tiên của nền nghệ thuật Việt Nam hiện đại với tân nhạc, thơ mới, hội họa, văn học hiện đại, điện ảnh, nhiếp ảnh.'),
(2, 'Lịch Sử', 'Dải đất nay là Hà Nội có dân cư từ vài ngàn năm trước nhưng cái tên gọi Hà Nội thì chỉ có từ năm 1831. Năm 1010, vua Lý Thái Tổ dời đô ra thành Ðại La, đổi gọi thành này là kinh đô Thăng Long. Kinh đô ngày ấy ứng với quận Hoàn Kiếm và một phần của hai quận Ðống Ða, Hai Bà Trưng ngày nay. Sau đó địa giới Thăng Long dần mở rộng và tới cuối thế kỷ 18 thì tương ứng với năm quận nội thành bây giờ. Năm 1802, nhà Nguyễn lên ngôi dời đô về Huế, Thăng Long không còn là Kinh đô nữa và ít lâu sau bị đổi gọi là phủ Hoài Ðức.  Năm 1831, có một cuộc cải cách hành chính lớn: xoá bỏ các trấn, thành lập các tỉnh. Từ đó ra đời tỉnh Hà Nội. Sở dĩ có tên gọi này vì tỉnh mới nằm trong (nội) hai con sông (hà) là sông Hồng và sông Ðáy, gồm có 4 phủ, 15 huyện. Tỉnh lỵ đặt tại thành Thăng Long cũ, do vậy Thăng Long được gọi là tỉnh thành Hà  Ngày 2/9/1945, tại quảng trường Ba Ðình, Chủ tịch Hồ Chí Minh đọc bản tuyên ngôn độc lập, chính thức khai sinh ra nước Việt Nam Dân chủ Cộng hoà và Hà Nội là thủ đô của đất nước.  Ðến năm 1975, với chiến dịch Hồ Chí Minh lịch sử, miền Nam hoàn toàn giải phóng, đất nước thống nhất toàn vẹn. Tháng 7/1976 tại kỳ họp đầu tiên Quốc hội khoá VI, Quốc hội thống nhất quyết định lấy Hà Nội là thủ đô nước Cộng hoà Xã hội chủ nghĩa Việt Nam ', '2.jpg', 'Dải đất nay là Hà Nội có dân cư từ vài ngàn năm trước nhưng cái tên gọi Hà Nội thì chỉ có từ năm 1831. Năm 1010, vua Lý Thái Tổ dời đô ra thành Ðại La, đổi gọi thành này là kinh đô Thăng Long. Kinh đô ngày ấy ứng với quận Hoàn Kiếm và một phần của hai quận Ðống Ða, Hai Bà Trưng ngày nay'),
(3, 'Các vùng của Hà Nội', 'Ngày 29/5/2008, kỳ họp thứ 3 Quốc hội khóa XII thông qua Nghị quyết về việc điều chỉnh địa giới hành chính thành phố Hà Nội và một số tỉnh liên quan. Nghị quyết có hiệu lực thi hành từ ngày 1/8/2008. Theo đó thành phố Hà Nội mở rộng có diện tích tự nhiên 334.470,02ha (3.344,7002km2) và dân số là 6.232.940 người.  Đây là lần mở rộng bao gồm toàn bộ diện tích, dân số hiện tại của thành phố Hà Nội (cũ) và toàn bộ diện tích 219.341,11ha (2.193,4111km2) và dân số 2.568.007 người của tỉnh Hà Tây (sau khi đã tách xã Tân Đức huyện Ba Vì về tỉnh Phú Thọ), diện tích và dân số huyện Mê Linh (tỉnh Vĩnh Phúc), diện tích và dân số của 4 xã Đông Xuân, Tiến Xuân, Yên Bình, Yên Trung của huyện Lương Sơn (tỉnh Hòa Bình).  Sau khi mở rộng, thành phố Hà Nội có 29 đơn vị hành chính trực thuộc bao gồm 10 quận, 18 huyện, 1 thị xã - và 577 đơn vị hành chính cấp xã - gồm 401 xã, 154 phường và 22 thị trấn.', '3.jpg', 'Ngày 29/5/2008, kỳ họp thứ 3 Quốc hội khóa XII thông qua Nghị quyết về việc điều chỉnh địa giới hành chính thành phố Hà Nội và một số tỉnh liên quan. Nghị quyết có hiệu lực thi hành từ ngày 1/8/2008. Theo đó thành phố Hà Nội mở rộng có diện tích tự nhiên 334.470,02ha (3.344,7002km2) và dân số là 6.232.940 người.'),
(4, 'Tổng Quan', 'Hà Nội là Thủ đô của Việt Nam, thuộc đồng bằng sông Hồng trù phú, nơi đây đã sớm trở thành một trung tâm chính trị và tôn giáo ngay từ những buổi đầu của lịch sử Việt Nam. Năm 1010, Lý Công Uẩn, vị vua đầu tiên của nhà Lý, quyết định xây dựng kinh đô mới ở vùng đất này với cái tên Thăng Long. Trong suốt thời kỳ của các triều đại Lý, Trần, Lê, Mạc, kinh thành Thăng Long là nơi buôn bán, trung tâm văn hóa, giáo dục của cả miền Bắc. Khi Tây Sơn rồi nhà Nguyễn lên nắm quyền trị vì, kinh đô được chuyển về Huế và Thăng Long bắt đầu mang tên Hà Nội từ năm 1831, dưới thời vua Minh Mạng. Trải qua hai cuộc chiến tranh, Hà Nội là thủ đô của miền Bắc rồi nước Việt Nam thống nhất và giữ vai trò này cho tới ngày nay.  Sau đợt mở rộng địa giới hành chính vào tháng 8 năm 2008, Hà Nội hiện nay có diện tích 3.324,92 km², gồm một thị xã, 10 quận và 18 huyện ngoại thành. Cùng với Thành phố Hồ Chí Minh, Hà Nội là một trong hai trung tâm kinh tế của cả quốc gia. Năm 2007, GDP của thành phố tăng khoảng 12,1%, tổng giá trị xuất khẩu đạt trên 4 tỷ USD, tổng thu ngân sách khoảng 45.709 tỷ đồng.  Theo kết quả cuộc điều tra dân số ngày 1 tháng 4 năm 2009, dân số Hà Nội là 6.448.837 người. Hà Nội cũng là một trung tâm văn hóa, giáo dục với các nhà hát, bảo tàng, các làng nghề truyền thống, những cơ quan truyền thông cấp quốc gia và các trường đại học lớn.', '4.jpg', 'Hà Nội là Thủ đô của Việt Nam, thuộc đồng bằng sông Hồng trù phú, nơi đây đã sớm trở thành một trung tâm chính trị và tôn giáo ngay từ những buổi đầu của lịch sử Việt Nam. Năm 1010, Lý Công Uẩn, vị vua đầu tiên của nhà Lý, quyết định xây dựng kinh đô mới ở vùng đất này với cái tên Thăng Long'),
(5, 'Thời Tiết', 'Khí hậu Hà Nội tiêu biểu cho vùng Bắc Bộ với đặc điểm của khí hậu nhiệt đới gió mùa ẩm, mùa hè nóng, mưa nhiều và mùa đông lạnh, ít mưa. Thuộc vùng nhiệt đới, thành phố quanh nǎm tiếp nhận lượng bức xạ Mặt Trời rất dồi dào và có nhiệt độ cao  Và do tác động của biển, Hà Nội có độ ẩm và lượng mưa khá lớn, trung bình 114 ngày mưa một năm. Một đặc điểm rõ nét của khí hậu Hà Nội là sự thay đổi và khác biệt của hai mùa nóng, lạnh. Mùa nóng kéo dài từ tháng 5 tới tháng 9, kèm theo mưa nhiều, nhiệt độ trung bình 29,2ºC. Từ tháng 11 tới tháng 3 năm sau là khí hậu của mùa đông với nhiệt độ trung bình 15,2ºC. Cùng với hai thời kỳ chuyển tiếp vào tháng 4 và tháng 10, thành phố có đủ bốn mùa xuân, hạ, thu và đông.', '5.jpg', 'Khí hậu Hà Nội tiêu biểu cho vùng Bắc Bộ với đặc điểm của khí hậu nhiệt đới gió mùa ẩm, mùa hè nóng, mưa nhiều và mùa đông lạnh, ít mưa. Thuộc vùng nhiệt đới, thành phố quanh nǎm tiếp nhận lượng bức xạ Mặt Trời rất dồi dào và có nhiệt độ cao'),
(6, 'Kinh Tế', 'Vị thế trung tâm kinh tế của Hà Nội đã được thiết lập từ rất lâu trong lịch sử. Tên những con phố như Hàng Bạc, Hàng Đường, Hang Than... đã minh chứng cho điều này. Tới thế kỷ gần đây, với sự phát triển mạnh mẽ của Sài Gòn và khu vực Nam Bộ, Hà Nội chỉ còn giữ vị trí quan trọng thứ hai trong nền kinh tế Việt Nam.  Sau một thời gian dài của thời kỳ bao cấp, từ đầu thập niên 1990, kinh tế Hà Nội bắt đầu ghi nhận những bước tiến mạnh mẽ. Tốc độ tăng trưởng GDP bình quân của thành phố thời kỳ 1991-1995 đạt 12,52%, thời kỳ 1996-2000 là 10,38%. Từ năm 1991 tới 1999, GDP bình quân đầu người của Hà Nội tăng từ 470 USD lên 915 USD, gấp 2,07 so với trung bình của Việt Nam. Theo số liệu năm 2000, GDP của Hà Nội chiếm 7,22% của cả quốc gia và khoảng 41% so với toàn vùng Đồng bằng sông Hồng.  Giai đoạn phát triển của thập niên 1990 cũng cho thấy Hà Nội đã có những thay đổi về cơ cấu kinh tế. Từ 1990 tới 2000, trong khi tỷ trọng ngành công nghiệp tăng mạnh từ 29,1% lên 38% thì nông-lâm nghiệp và thủy sản từ 9% giảm xuống còn 3,8%. Tỷ trọng ngành dịch vụ cũng giảm trong khoảng thời gian này, từ 61,9% xuống còn 58,2%. Ngành công nghiệp của Hà Nội vẫn tập trung vào 5 lĩnh vực chính, chiếm tới 75,7% tổng giá trị sản xuất công nghiệp, là cơ-kim khí, điện-điện tử, dệt-may-giày, chế biến thực phẩm và công nghiệp vật liệu. Bên cạnh đó, nhiều làng nghề truyền thống như gốm Bát Tràng, may ở Cổ Nhuế, đồ mỹ nghệ Vân Hà... cũng dần phục hồi và phát triển.  Năm 2007, GDP bình quân đầu người của Hà Nội lên tới 31,8 triệu đồng, trong khi con số của cả Việt Nam là 13,4 triệu. Hà Nội là một trong những địa phương nhận được đầu tư trực tiếp từ nước ngoài nhiều nhất, với 1.681,2 triệu USD và 290 dự án. Thành phố cũng là địa điểm của 1.600 văn phòng đại diện nước ngoài, 14 khu công nghiệp cùng 1,6 vạn cơ sở sản xuất công nghiệp. Nhưng đi đôi với sự phát triển kinh tế, những khu công nghiệp này đang khiến Hà Nội phải đối mặt với vấn đề ô nhiễm môi trường. Bên cạnh những công ty nhà nước, các doanh nghiệp tư nhân cũng đóng vai trò quan trọng trong nền kinh tế Hà Nội. Năm 2003, với gần 300.000 lao động, các doanh nghiệp tư nhân đã đóng góp 77% giá trị sản xuất công nghiệp cùa thành phố. Ngoài ra, 15.500 hộ sản xuất công nghiệp cũng thu hút gần 500.000 lao động. Tổng cộng, các doanh nghiệp tư nhân đã đóng góp 22% tổng đầu tư xã hội, hơn 20% GDP, 22% ngân sách thành phố và 10% kim ngạch xuất khẩu của Hà Nội.  Sau khi mở rộng địa giới hành chính, với hơn 6 triệu dân, Hà Nội có 3,2 triệu người đang trong độ tuổi lao động.[66] Mặc dù vậy, thành phố vẫn thiếu lao động có trình độ chuyên môn cao. Nhiều sinh viên tốt nghiệp vẫn phải đào tạo lại, cơ cấu và chất lượng nguồn lao động chưa dịch chuyển theo yêu cầu cơ cấu ngành kinh tế. Hà Nội còn phải đối đầu với nhiều vấn đề khó khăn khác. Năng lực cạnh tranh của nhiều sản phẩm dịch vụ cũng như sức hấp dẫn môi trường đầu tư của thành phố còn thấp. Việc chuyển dịch cơ cấu kinh tế vẫn chậm, đặc biệt cơ cấu nội ngành công nghiệp, dịch vụ và các sản phẩm chủ lực mũi nhọn. Chất lượng quy hoạch phát triển các ngành kinh tế ở Hà Nội không cao và thành phố cũng chưa huy động tốt tiềm năng kinh tế trong dân cư.', '6.jpg', 'Vị thế trung tâm kinh tế của Hà Nội đã được thiết lập từ rất lâu trong lịch sử. Tên những con phố như Hàng Bạc, Hàng Đường, Hang Than... đã minh chứng cho điều này. Tới thế kỷ gần đây, với sự phát triển mạnh mẽ của Sài Gòn và khu vực Nam Bộ, Hà Nội chỉ còn giữ vị trí quan trọng thứ hai trong nền kinh tế Việt Nam');

