CREATE TABLE IF NOT EXISTS `tainguyendulich` (
  `matn` int(10) NOT NULL AUTO_INCREMENT,
  `tentn` varchar(100) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `chitiet` varchar(2000) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `anh` varchar(50) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `malh` int(10) NOT NULL,
  PRIMARY KEY (`matn`)
) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=46 ;

--
-- Dumping data for table `tainguyendulich`
--

INSERT INTO `tainguyendulich` (`matn`, `tentn`, `chitiet`, `anh`, `malh`) VALUES
(3, 'Thành Cổ Loa', 'Từ khi dựng nước, các vua Hùng đã xây dựng kinh đô tại Bạch hạc, Việt Trì. Đến đời Thục Phán dời Bạch Hạc xuôi dòng chọn vùng đất, gò đồi vây bọc ở trại Phong Khê quận Vũ Ninh (nay là Cổ Loa) để đóng đô. Trong công cuộc định đô này, Thục Phán phải bỏ rất nhiều công sức như việc chuyển dân đến để xây thành đắp lũy.\r\n\r\nThành rộng hơn ngàn trượng, như hình con ốc nên gọi Loa Thành, lại có tên là Tư Long. Thành cứ đắp xong lại sụt. Vua lấy làm lo, bèn sai khấn trời đất và thần linh sông núi rồi hưng công xây lại. Vòng thành chạy dài hàng chục km, đắp xoáy nhiều tầng. Chân thành cao tới hàng chục thước. Đồ sộ như vậy, nên thời Đường, người ta gọi là thành Côn Luân.\r\n\r\nVới số dân it ỏi, công việc đắp thành thật quá sức. Người ta tưởng như có thần phù giúp, nên có truyền thuyết: Năm 255 TCN, An Dương Vương đang băn khoăn cứ xây thành xong lại đổ. Trong lúc ấy, có người xuất hiện - chỉ tay vào cổng thành nói: Thành này xây bao giờ mới xong? Vua gặng hỏi tại sao, người ấy đủng đỉnh trả lời: "Hãy đợi Giang sứ đến thì biết!". Nói xong, người ấy từ biệt đi ngay.\r\n\r\nSáng sớm hôm sau, Vua ra cổng thành thì thấy nổi lên một con rùa vàng tự xưng là Giang sứ và nói được tiếng người. Vua sai đặt Giang sứ vào chiếc mâm vàng để trước điện để giúp việc. Sau đó, Giang sứ giúp vua trị được yêu quái ở núi Thất Diệu (núi Sái). Từ đó, thành Cổ Loa xây đến đâu được đến đấy. Thành xây xong Giang sứ tặng cho Vua chiếc móng của mình để Vua chế nỏ thần chống giặc.\r\n\r\nVị thần kia là ai - nếu không phải là Huyền Thiền Thượng đế Đăng Ma Thiên Tôn. Còn Giang sứ là thần Kim Quy - một bộ tướng của Huyền Thiên Thượng Đế.\r\nHiện nay, ở núi Thất Diệu phía bắc thành Cổ Loa vẫn còn ngôi quán thờ Huyền Thiên. Quán ấy có tên "Huyền Thiên Đại Quán". Trong quán có tượng Huyền Thiên, tượng rùa và rắn giống như tượng thờ ở Trấn Vũ quán bên hồ Tây, Hà Nội.\r\n\r\nThành Cổ Loa chủ yếu được đắp bằng đất, kè đá, rải gốm. Là công trình kiến trúc quân sự đồ sộ, to lớn. Thục Phán lợi dụng tối đa điều kiện địa lý vùng này là ', 'coloa.jpg', 1),
(4, 'Côn Sơn-Kiếp Bạc', '   Côn Sơn - Kiếp Bạc là quần thể di tích lịch sử văn hóa danh thắng đặc biệt quan trọng của Quốc gia. Nơi đây gắn bó với cuộc đời, sự nghiệp của nhiều danh nhân nổi tiếng: ba vị Phật tổ của thiền phái phật giáo Trúc Lâm thời Trần: Đệ nhất tổ Hoàng đế Điều Ngự Giác Hoàng Trần Nhân Tông, Đệ nhị tổ Pháp Loa, Đệ tam tổ Huyền Quang, Trần Hưng Đạo, Trần Nguyên Đán, Nguyễn Trãi...Uy đức của họ đã góp phần hun đúc lên hồn thiêng sông núi, để lại tiếng vang muôn thủa. Trải qua hơn 700 năm tồn tại, phát triển Côn Sơn - Kiếp Bạc là một trong những trung tâm tôn giáo, tín ngưỡng lớn của đất nước. Vì vậy, đây là cái nôi sản sinh và hội tụ của nhiều giá trị văn hóa phi vật thể đặc sắc của cư dân đồng bằng Bắc bộ và các vùng miền trong cả nước. Từ phong tục tập quán đến các nghi lễ truyền thống, các trò chơi, diễn xướng, làng nghề...đều mang đậm bản sắc văn hóa của một vùng.\r\n\r\n       Ngoài hai khu di tích Chùa Côn Sơn- Đền Kiếp Bạc, nơi đây còn có hệ thống di sản văn hoá rất phong phú như: Đền Sinh, Đền Hoá, Chùa - Đền Nam Tào, Chùa - Đền Bắc Đẩu, Chùa Mít, Đình Thanh Tảo, Nghè Dím, Nghè Nẫm cùng hệ thống di tích gắn với những chiến công hào hùng của dân tộc qua các cuộc kháng chiến bảo vệ Quốc gia Đại Việt của các triều đại Việt Nam: hệ thống sông Lục Đầu, núi Phượng Hoàng, núi Ngũ Nhạc, núi Côn Sơn, núi Dược Sơn, khu di tích đền chùa Trung Quê, Hang Tiền, Hố Thóc, Xóm Hống, Xưởng Thuyền, Hành Cung, Sông Vang...\r\n\r\n      Gắn với hệ thống di tích lịch sử văn hoá và cảnh quan thiên nhiên này là kho tàng văn hoá phi vật thể phong phú về loại hình, đa dạng về chức năng, chứa đựng trong đó là dấu ấn, tình cảm của lớp người lao động bình dân đến tư tưởng tri thức của các bậc hiền tài, các vị quân vương, thánh thần tiêu biểu của các triều đại phong kiến.\r\n\r\n      Hệ thống di sản văn hoá phi vật thể ở khu di tích Côn Sơn- Kiếp Bạc qua nghiên cứu sưu tầm có thể thống kê, phân loại ra một số loại hình cơ bản như sau:\r\n\r\n      1. Phong tục tập quán: ăn, ở, mặc, tang ma, cưới xin…\r\n\r\n     ', 'conson.jpg', 1),
(5, 'Cột cờ Hà Nội', 'Là một trong năm di tích còn được bảo tồn nguyên vẹn trong quần thể khu di tích thành cổ, cột cờ Hà Nội là một trong những biểu tượng thiêng liêng của mảnh đất Thăng Long, Hà Nội.\r\n\r\nTrên chiếc xe đạp cũ kỹ, một chiều tháng thu tháng 10 tôi đến với cột cờ Hà Nội như một cách tìm về nơi từng gắn với những chiến công hiển hách của dân tộc.\r\nTừ phần cửa sổ ở đỉnh cột cờ người ta có thể quan sát toàn bộ thành phố cả vùng nội và ngoại thành. Đó cũng chính là lý do thực dân Pháp đã không những không cho phá hủy công trình này trong thời gian tạm chiếm từ 1894 - 1897 mà còn dùng cột cờ làm đài quan sát và đặt trạm thông tin liên lạc giữa ban chỉ huy với các đơn vị xung quanh bằng cờ và đèn tín hiệu.\r\n\r\nTheo Phan Thúc Trực trong sách Quốc sử di biên, thì “ở phía trước thành Thăng Long người ta cho xây một chiếc cột gọi là “điện đài” cao hơn trăm thước”. Theo tính toán 1 thước bằng 0,42m, vậy điện đài trong ghi chép của Phan Thúc Trực sẽ có chiều cao khoảng 42m.\r\n\r\nCòn theo hiện trạng được cơ quan quản lý cung cấp thì cột cờ Hà Nội là một công trình bề thế với ba tầng đế giật cấp và một than cột. Các tầng đế hình chóp vuông cụt, nhỏ dần chồng lên nhau xung quanh xây ốp bằng gạch.\r\n\r\nTầng một mỗi chiều dài 42,5m, cao 3,1m, có hai cầu thang gạch dẫn lên. Tầng hai mỗi chiều dài 27m, cao 3,7m, có bốn cửa, cửa hướng đông trên có đắp hai chữ “Nghênh húc”, nghĩa là đón nắng ban mai, cửa Tây đắp hai chữ “Hồi quang”, nghĩa là ánh sáng phản chiếu, cửa Nam có hai chữ “hướng minh”, nghĩa là hướng về ánh sáng, còn cửa Bắc không có chữ.\r\n\r\nTầng ba mỗi chiều dài 12,8m, cao 5,1m, có cửa lên cầu thang trông về hướng bắc. Cột cờ hình trụ tám cạnh, thon từ dưới lên. Phần thân cột cao 12,8m, mỗi cạnh đáy chừng 21,1m. Trong thân cột cờ có 54 bậc thang xoáy ốc lên tận đỉnh. Toàn thân của cột cờ được soi sáng và thông hơi bằng 39 cửa nhỏ hình hoa thị và sáu cửa hình dẻ quạt. Những cửa này được đặt dọc các cạnh, mỗi cạnh có chừng 4-5 cửa.\r\n\r\nĐỉnh của cột cờ có hình bát giác, cao 3,3m, có 8 cửa sổ t', 'CotCo.jpg', 1),
(6, 'Hoàng Thành Thăng Long', 'Lý Công Uẩn lên ngôi vua, sáng lập vương triều Lý (1009 - 1225) tại kinh đô Hoa Lư (Ninh Bình) ngày 2-11 Kỷ Dậu (21 – 11 - 1009). Tháng 7 mùa thu năm 1010, nhà vua dời đô từ Hoa Lư về thành Đại La và đổi tên là Thăng Long. Ngay trong mùa thu năm đó, nhà Lý đã khẩn trương xây dựng một số cung điện làm nơi ở và làm việc của vua, triều đình và hoàng gia. Trung tâm là điện Càn Nguyên, nơi thiết triều của nhà vua, hai bên có điện Tập Hiền và Giảng Võ, phía sau là điện Long An, Long Thụy làm nơi vua nghỉ. Đến cuối năm 1010, 8 điện 3 cung đã hoàn thành. Những năm sau, một số cung điện và chùa tháp được xây dựng thêm. Một vòng thành bao quanh các cung điện cũng được xây đắp trong năm đầu, gọi là Long Thành hay Phượng Thành. Đó chính là Hoàng Thành theo cách gọi phổ biến về sau này. Thành đắp bằng đất, phía ngoài có hào, mở 4 cửa: Tường Phù ở phía đông, Quảng Phúc ở phía tây, Đại Hưng ở phía nam, Diệu Đức ở phía bắc. Tuy còn những ý kiến khác nhau, nhưng căn cứ vào sử liệu và di tích còn lại, có thể xác định cửa Tường Phù mở ra phía Chợ Đông và khu phố buôn bán tấp nập của phường Giang Khẩu và đền Bạch Mã. Cửa Quảng Phúc mở ra phía chùa Diên Hựu (chùa Một Cột) và chợ Tây Nhai (chợ Ngọc Hà). Cửa Đại Hưng ở khoảng gần Cửa Nam hiện nay. Cửa Diệu Đức nhìn ra trước sông Tô Lịch, khoảng đường Phan Đình Phùng hiện nay. Trong Long Thành có một khu vực được đặc biệt bảo vệ gọi là Cấm Thành là nơi ở và nghỉ ngơi của vua và hoàng gia. Trong đời Lý, các kiến trúc trong Hoàng Thành còn qua nhiều lần tu sửa và xây dựng thêm.\r\n\r\nLong Thành và Cấm Thành là trung tâm chính trị của Kinh Thành. Phía ngoài, cùng với một số cung điện và chùa tháp là khu vực cư trú, buôn bán, làm ăn của dân chúng gồm các bến chợ, phố phường và thôn trại nông nghiệp. Một vòng thành bao bọc toàn bộ khu vực này bắt đầu được xây đắp từ năm 1014, gọi là thành Đại La hay La Thành. \r\n Vòng thành này vừa làm chức năng thành luỹ bảo vệ, vừa là đê ngăn lũ lụt. Thành được đắp mới và có tận dụng, tu bổ một phần thành Đại La ', 'hoangthanhthanglong.jpg', 1),
(7, 'Lăng Hồ Chủ Tịch', 'Lăng Chủ tịch Hồ Chí Minh, hay còn gọi là Lăng Hồ Chủ tịch, Lăng Bác, là nơi đặt thi hài của Chủ tịch Hồ Chí Minh. Lăng Chủ tịch Hồ Chí Minh được chính thức khởi công ngày 2 tháng 9 năm 1973, tại vị trí của lễ đài cũ giữa Quảng trường Ba Đình, nơi Người đã từng chủ trì các cuộc mít tinh lớn.\r\n\r\nLăng được khánh thành vào ngày 29 tháng 8 năm 1975. Lăng gồm 3 lớp với chiều cao 21,6 mét, lớp dưới tạo dáng bậc thềm tam cấp, lớp giữa là kết cấu trung tâm của lăng gồm phòng thi hài và những hành lang, những cầu thang lên xuống. Quanh bốn mặt là những hàng cột vuông bằng đá hoa cương, lớp trên cùng là mái lăng hình tam cấp. Ở mặt chính có dòng chữ: "Chủ tịch Hồ-Chí-Minh" bằng đá hồng màu mận chín. Trong di chúc, Hồ Chí Minh muốn được hỏa táng và đặt tro tại ba miền đất nước[1][2]. Tuy nhiên, theo nguyện vọng và tình cảm của nhân dân, Bộ Chính trị Ban Chấp hành Trung ương Đảng Cộng sản Việt Nam khóa III quyết định giữ gìn lâu dài thi hài Hồ Chí Minh để sau này nhân dân cả nước, nhất là nhân dân miền Nam, khách quốc tế có thể tới viếng Người.\r\nTrên đỉnh lăng là hàng chữ "Chủ tịch Hồ Chí Minh" ghép bằng đá ngọc màu đỏ thẫm của Cao Bằng[6]. Cửa lăng làm từ các cây gỗ quý từ Tây Nguyên. Tiền sảnh ốp đá hoa cương vân đỏ hồng, làm nền cho dòng chữ "Không có gì quý hơn Độc lập Tự do" và chữ ký của Hồ Chí Minh được dát bằng vàng. 200 bộ cửa trong Lăng được làm từ các loại gỗ quí do nhân dân Nam Bộ, Tây Nguyên, Quảng Nam - Đà Nẵng, và bộ đội Trường Sơn gửi ra, và do các nghệ nhân nghề mộc của Nam Hà, Hà Bắc, và Nghệ An thực hiện. Cánh cửa vào phòng đặt thi hài do hai cha con nghệ nhân ở làng Gia Hòa đóng.[6]  Hai bên cửa chính là hai cây hoa đại. Phía trước và phía sau lăng trồng 79 cây vạn tuế tượng trưng cho 79 năm trong cuộc đời của Hồ Chủ tịch. Hai bên phía nam và bắc của lăng là hai rặng tre, loại cây biểu tượng cho nước Việt Nam. Trước cửa lăng luôn có hai người lính đứng gác, 1 giờ đổi gác một lần.\r\nChính giữa lăng là phòng đặt thi hài ốp đá cẩm thạch Hà Tây. Trên tường có 2 l', 'langbacho.jpg', 1),
(8, 'Ngã Ba Đồng Lộc', 'Ngã ba Đồng Lộc nằm trên đường Trường Sơn, thuộc địa phận Đồng Lộc,Can Lộc, Hà Tĩnh. Ngã ba Đồng Lộc có tổng diện tích 50ha  nằm gọn trong  một thung lũng hình tam giác, hai bên đồi núi trọc, giữa là con đường độc đạo,mặt đường giống như một lòng máng, bom địch thả xuống bên nào đất đá cũng lăn xuống đường làm cản trở giao thông.\r\nTất cả mọi con đường từ  Bắc vào Nam đều phải vượt qua ngã ba Đồng Lộc. Ngã ba Đồng Lộc được coi như cổ họng, vượt qua được sẽ phân tán toả ra nhiều tuyến đường khác nhau đi vào nam. Ngã ba Đồng Lộc là trọng điểm có tầm quan trọng chiến lược nên trong chiến tranh phá hoại kẻ địch âm mưu ném bom huỷ diệt nhằm chặn đứt sự chi viện sức người, sức của, vũ khí, súng đạn, lương thực... của hậu phương lớn miền Bắc xã hội chủ nghĩa đối với tiền tuyến lớn miền Nam. Chỉ tính riêng 240 ngày đêm từ tháng 3 đến tháng 10/1968 không quân địch đã trút xuống đây 48.600 quả bom các loại.\r\n\r\nHồi 17h ngày 24/7/1968 tiểu đội 4 thanh niên xung phong được lệnh trọng điểm ở khu vực địch vừa thả bom để san lập hố bom sửa chữa đường, kết hợp sửa chữa hầm trú ẩn khơi sâu rãnh thoát nước ở đoạn đường độc đạo để nhanh chóng thông đường cho xe qua. Tiểu đội 4 hôm ấy có 10 cô gái trẻ:\r\n\r\nVõ thị Tần - 22 tuổi - tiểu đội trưởng\r\nHồ Thị Cúc - 21 tuổi - tiểu đội phó\r\nVõ Thị Hợi - 20 tuổi - chiến sĩ\r\nNguyễn Thị Xuân - 20 tuổi - chiến sĩ\r\nDương Thị Xuân - 19 tuổi - chiến sĩ\r\nTrần Thị Rạng - 19 tuổi - chiến sĩ\r\nHà Thị Xanh - 18 tuổi - chiến sĩ\r\nNguyễn Thị Nhỏ -  19 tuổi - chiến sĩ\r\nVõ Thị Hạ - 19 tuổi - chiến sĩ\r\nTrần Thị Hường - 17 tuổi - chiến sĩ\r\n\r\nNhận nhiệm vụ xong, các cô đến tại hiện trường gấp rút triển khai công việc với cả niềm vui được gửi gắm trên từng chiếc xe qua nên các cô không hề sợ hãi. Họ làm việc không ngơi tay, vừa cười, vừa nói, vừa ý ới gọi nhau. Bỗng một tốp máy bay phản lực từ h ướng Bắc vào Nam vượt qua trọng điểm. Tất cả các cô nhanh chóng nằm rạp xuống đường. Hết tiếng máy bay các cô lại chồm dậy làm việc. Bất ngờ tốp máy bay phản lực quay lại bay', 'ngabadongloc.jpg', 1),
(9, 'Nhà tù Hỏa Lò', 'Nhà tù Hỏa Lò nằm ngay trung tâm TP Hà Nội được xây dựng từ năm 1896. Thực dân Pháp xây dựng nhà tù này nhằm giam  những người đấu tranh chống chế độ thực dân Pháp.\r\nNhà tù Hỏa Lò vốn có tên là Đề lao Trung ương (Maison Centrale),nhưng do được xây trên đất của làng Phụ Khánh, tổng Vĩnh Xương (cũ), đây là làng nghề chuyên sản xuất đồ gốm, ngày đêm rực lửa lò nung vì thế làng còn có tên là Hỏa Lò và nhà tù ở đây cũng được gọi là nhà tù Hỏa Lò.\r\n\r\nCác nhà tù khác thường biệt lập với khu dân cư, riêng Hỏa Lò nằm tại trung tâm Hà Nội, thủ phủ của chính quyền thực dân khi đó.\r\n\r\nBên cạnh nhà tù là tòa đại hình và sở mật thám, tạo thành thế chân kiềng, sẵn sàng đàn áp phong trào Cách mạng của nhân dân ta. Hỏa Lò là công trình kiên cố vào loại bậc nhất Đông Dương, đến con kiến cũng khó lòng qua nổi.\r\n\r\nBao quanh nhà tù là bức tường bằng đá, cốt thép cao 4m, dày 0,5m, được gia cố bởi hệ thống dây thép gai có dòng điện cao thế chạy qua, bốn góc là những tháp canh có khả năng quan sát nhất cử nhất động toàn bộ trại giam.\r\n\r\nRiêng hệ thống cửa sắt, khóa được mang từ Pháp sang. Các phòng giam, phòng tối, xà lim chật chội, thiếu không khí và những tên cai ngục khét tiếng, có thâm niên cai quản nhà tù sẵn sàng đàn áp, thậm chí cướp đi sinh mạng của tù nhân.\r\n\r\nNhà tù Hỏa Lò thực sự là địa ngục trần gian. Ngay từ khi chưa hoàn thành, tháng 1-1899 nhà tù Hỏa Lò đã đảm nhận việc giam người. Theo thiết kế ban đầu, Hoả Lò chỉ đủ giam 500 tù nhân, nhưng nó đã nhiều lần được mở rộng để có thêm chỗ giam giữ tù nhân. Những năm 1950- 1953, Hỏa Lò giam cầm tới 2.000 người tù.\r\n\r\nHiện nay, khu di tích Hỏa Lò còn lưu giữ chiếc máy chém đã được thực dân Pháp dùng lưu động, tháng 1-1930 được vận chuyển lên Yên Bái để hành hình 13 chiến sĩ Việt Nam Quốc dân đảng bị bắt trong cuộc khởi nghĩa Yên Bái (đứng đầu là Nguyễn Thái Học).\r\n\r\nNhiều thế hệ người Việt Nam đã bị giam cầm tại nhà tù Hỏa Lò, trong đó có các nhà nho yêu nước như cụ Phan Bội Châu, Lương Văn Can, Dương Bá Trạc đến các đồn', 'nhatuhoalo.jpg', 1),
(10, 'Nhà tù Sơn La', 'Nhà tù Sơn La do Thực dân Pháp xây dựng năm 1908 với diện tích ban đầu là 500 m2, chủ yếu để giam cầm tù thường phạm.\r\nNgày 3-2-1930 Đảng cộng sản Việt Nam ra đời và lãnh đạo toàn dân tộc Việt Nam tiến hành cuộc cách mạng dân tộc dân chủ nhân dân. Hoảng sợ trước phong trào đấu tranh giành độc lập dân tộc của cách mạng Việt Nam ngày càng dâng cao. Thực dân Pháp đã mở rộng nhà tù Sơn La thêm 1.500m2 vào năm 1930 và 1.700m2 vào năm 1940.Thực dân Pháp đã biến nơi đây thành địa ngục để giam cầm, đầy ải và thủ tiêu ý chí đấu tranh của những người Cộng sản Việt Nam. Nhưng cũng chính tại nơi đây, hơn bao giờ hết khí tiết của những người chiến sỹ cộng sản đã toả sáng và thắp lên ngọn lửa đấu tranh cách mạng khắp núi rừng Tây Bắc, nơi đây đã trở thành một trường học cách mạng vĩ đại rèn luyện và bổ sung cho Đảng, cho cách mạng Việt Nam những chiến sỹ cộng sản như các đồng chí: Tô Hiệu, Lê Duẩn, Trường Chinh, Nguyễn Lương Bằng, Văn Tiến Dũng, Lê Đức Thọ, Nguyễn Văn Trân và bao đồng chí trung kiên khác.Năm 1952, khi thực dân Pháp rút khỏi Sơn La, đã ném bom nhằm xóa đi dấu vết tội ác của chúng, lần 2 vào năm 1965, đế quốc Mỹ đã dánh phá Thị xã Sơn La phá hủy một phần của nhà tù. Năm 1980, Bảo tàng Sơn La tiến hành phục chế lại lần thứ nhất: San lấp  hố bom, xây dựng lại một số đoạn tường rào bao quanh; lần thứ 2, vào năm 1994, phục chế lại 2 tháp canh, nhà bếp, trại giam lớn, gia cố lại hầm ngầm, xây dựng các bức tường của các phòng giam theo dấu vết của các nền móng cũ. Năm 1994, Bảo tàng Sơn La đưa ra ý định phục chế lại toàn bộ khu di tích lại như nguyên dạng ban đầu, nhưng không sưa tầm được đầy đủ hồ sơ, nên không đủ cơ sở khoa học để khôi phục lại toàn bộ mà chỉ xây các bức tường lên cao một chút để khách tham quan có thể hình dung được cấu trúc của khu nhà tù Sơn La.Trong 1007 lượt tù nhân bị giam cầm ở nhà tù Sơn La, hiện nay Bảo tàng Sơn La đã sưu tầm và lưu giữ 250 hồ sơ gốc của tù nhân; danh sách 61 liệt sĩ; danh sách 870 tù nhân, danh sách 180 người đã được rèn lu', 'nhatusonla.jpg', 1),
(11, 'Phố cổ Hà Nội', 'Khu Phố cổ Hoàn Kiếm là nơi hội tụ 36 phố phường có bề dày gần một ngàn năm lịch sử của một khu đô thị buôn bán sầm uất.\r\nKhu phố mang đậm trong mình những dấu vết lịch sử. Các phố mang tên các mặt hàng được sản xuất hoặc bày bán ở đó: phố Hàng Bông, phố Hàng Gai, phố Lò Rèn, phố Hàng Đường, ...\r\n\r\nMạng lưới đô thị phản ánh cơ cấu tổ chức thành thị cổ xưa gồm 36 phường nghề . Cơ cấu này về mặt không gian và xã hội là hiện thân của một di sản phi vật chất đặc biệt, duy trì các nghề cổ và giới thiệu nhiều hoạt động mang tính chất truyền thống tại các khu phố. Vì vậy, khu đô thị rất náo nhiệt  với việc các thợ thủ công làm việc hay bán hàng trên phố hay trong các nhà hàng nhỏ, người bán hàng với nhiều loại mặt hàng và các cửa hàng được bày bán trên vỉa hè.\r\n\r\nKhông những vậy, vẫn còn một di sản giàu kiến trúc đang tồn tại. Nhiều ngôi nhà cổ như những ngôi nhà ở có nhiều giá trị, đình, đền thờ và nhiều ngôi chùa đã minh chứng cho điều đó. Kiến trúc của khu phố cổ được thể hiện đặc biệt qua 3 phong cách: cách xây dựng theo kiểu truyền thống của Việt Nam hoặc Trung Quốc, kiến trúc thuộc địa kiểu Pháp và phong cách nghệ thuật trang trí. Ngày nay, khu Phố cổ đang tận dụng cơ hội phát triển kinh tế và đón nhận một lượng khách du lịch rất lớn : các quán cà-phê, nhà hàng, cửa hàng bán đồ lưu niệm thủ công và các khách sạn nhỏ đã lần lượt ra đời. Một số nghề như nghề thủ công lụa tơ tằm và buôn bán kim hoàn đã có những bước phát triển vượt bậc.\r\n\r\nNgười Hà Nội vẫn gắn bó với lịch sử và di sản của thành phố hiện đang bị xuống cấp do bối cảnh nền kinh tế đang trên đà phát triển. Để bảo tồn di sản của khu phố cổ, Bộ xây dựng Việt Nam ngay từ năm 1995 đã ra quyết định về nguyên tắc bảo tồn và trùng tu khu Phố cổ.\r\n\r\nBộ văn hoá và thông tin Việt Nam đã xếp hạng Khu phố cổ danh hiệu Di sản lịch sử của quốc gia ngày 5 tháng 4 năm 2004.', 'phocohanoi.jpg', 1),
(12, 'Phủ Giầy-Nam Định', 'Phủ Giầy là tên gọi chung cho các di tích thờ bà Chúa Liễu Hạnh thuộc xã Kim Thái, huyện Vụ Bản. Đây là quần thể di tích được xây dựng trong một khu vực địa lý có nhiều dấu vết văn hoá của cư dân Việt xưa và nay. Cách đó không xa có núi Lê, núi Gôi, với các hang động nơi cư trú của Người tiền sử. Với những di vật văn hoá thời kỳ đồ đá: Rìu đá, cuốc đá... là những dấu vết văn hoá, chứng tỏ sự xuất hiện khá sớm của con người trên mảnh đất này! Điều này, dễ dàng giải thích cho việc bảo lưu những dấu vết văn hoá bản địa, những tín ngưỡng dân gian thuần Việt, Phủ Giầy là một hiện tượng tín ngưỡng dân gian thuần Việt, tồn tại và có sức hấp dẫn khách hành hương hàng vài ba thế kỷ nay.\r\n\r\nKhách hành hương đến với Phủ Giầy, trước hết, hãy vượt hàng trăm bậc đá, lên đỉnh núi Tiên Hương thăm đền Thượng (còn gọi là đền Mẫu Thượng Ngàn). Năm 1857, tiến sĩ Lê Hi Vĩnh đã viết đôi câu đối:\r\n\r\n"Thái tông thiệu bình nguyên niên, Phạm Gia Khải thánh"\r\nThế tông Quang Hưng sơ thế Thái lĩnh lập từ\r\n\r\nTạm dịch:\r\n\r\nĐời Thái Tông niên hiệu Thiệu Bình, năm đầu họ Phạm Sinh ra bậc thánh (1434)\r\nĐời Thế Tông, niên hiệu Quang Hưng năm đầu, dựng đền ở núi Thái (1578)\r\n\r\nNhư vậy, núi Tiên Hương còn có tên gọi là núi An Thái.\r\n\r\nPhía Nam đền Thượng, trên một quả đồi nhỏ có ngôi chùa cổ, ở đó có cây hương đá (khắc bài kinh cúng phật) từ đầu thế kỷ XVIII và cây tháp 14 tầng, phong cách kiến trúc thời Nguyễn...\r\n\r\nGần núi Tiên Hương còn có đền thờ Thiền Sư Không Lộ, nhân dân thường gọi là đình ông Khổng, và một số khu di tích khác có liên quan đến khu di tích Phủ Giầy.\r\n\r\nKhu di tích Phủ Giầy từ bao đời nay thu hút khách du lịch hành hương trên khắp mọi nẻo đường về đây, ngoài yếu tố tín ngưỡng, di tích này còn có giá trị rất cao về kiến trúc, nghệ thuật thực sự còn được coi là tài sản văn hoá của dân tộc nói chung và Nam Hà nói riêng.\r\n\r\nNguyên xưa kia, hai thôn Vân Cát và Tiên Hương là một. Ngôi phủ thờ "Tam toà thánh mẫu" ở An Thái, huyện Thiên Bản còn đơn sơ, được xây dựng từ thời Lê Cảnh Trị (16', 'phugiay.jpg', 1),
(13, 'Tân Trào', 'Tân Trào là xã nằm ở đông bắc huyện Sơn Dương, gắn liền với tên tuổi sự nghiệp vị lãnh tụ vĩ đại của Cách mạng Việt Nam,danh nhân văn hoá thế giới, Hồ Chí Minh. Đến nay Tân Trào có trên 17 di tích. Nơi đây đã ghi lại những sự kiện lịch sử hào hùng của ngày đầu thành lập nước Việt Nam Dân chủ Cộng hoà. Những di tích chính của Tân Trào gồm: Lán Nà Lừa, cây đa Tân Trào, đình Tân Trào, đình Hồng Thái… Cùng với những di tích lịch sử ghi dấu ấn cuộc kháng chiến thần thánh của dân tộc ta suốt 9 năm trường kỳ. Khu di tích văn hóa- lịch sử và sinh thái Quốc gia Tân Trào là tâm điểm của những chuyến du lịch về nguồn.     \r\n1. Lán Nà Lừa\r\nLà nơi Bác Hồ ở và làm việc từ cuối tháng 5 đến cuối tháng 8 năm 1945 để chuẩn bị cho cuộc Tổng khởi nghĩa. Lán được dựng bằng tre theo kiểu nhà sàn. Ngày mùng 4 tháng 6 năm 1945, tại nơi đây, Bác Hồ đã triệu tập Hội nghị cán bộ để chuẩn bị thành lập “Khu giải phóng, Quân giải phóng”, tiến tới Quốc dân Đại Hội, Tổng khởi nghĩa. Hiện tại lán vẫn được bảo tồn và là điểm du lịch hấp dẫn du khách thăm quan.\r\n\r\n2. Cây Đa Tân Trào\r\nDưới bóng cây Đa của làng Tân Lập, chiều ngày 16 tháng 8 năm 1945, quân Giải phóng Việt Nam làm lễ xuất quân trước sự chứng kiến của nhân dân Tân Trào và 60 Đại biểu toàn quốc về dự Quốc dân Đại hội. Đồng chí Võ Nguyên Giáp đã đọc Bản Quân Lệnh số 1 và ngay sau đó quân Giải phóng đã lên đường qua Thái Nguyên tiến về giải phóng Hà Nội. \r\n3. Đình Tân Trào\r\nLà một ngôi đình thờ Thành Hoàng và các vị thần sông, thần núi của làng Tân Lập. Đình được dựng vào năm Quý Hợi (1923) theo kiểu nhà sàn, cột gỗ 3 gian 2 chái, mái lợp lá cọ, Dưới mái đình này, ngày 16 tháng 8 năm 1945, các Đại biểu trên khắp mọi miền Tổ quốc đã về họp Quốc dân Đại hội. Tại đây, các Đại biểu đã tán thành chủ trương tiến hành Tổng khởi nghĩa của Đảng, thông qua Lệnh khởi nghĩa và 10 chính sách lớn của Việt Minh, quy định quốc kỳ là lá cờ đỏ sao vàng, quốc ca là bài Tiến quân ca và cử ra Uỷ ban Giải phóng Dân tộc Việt Nam tức Chính phủ Lâm thời do đồng ch', 'tantrao.jpg', 1),
(14, 'Thành cổ Sơn Tây', 'Thành Sơn Tây nằm giữa thành phố Sơn Tây cách Hà Nội hơn 40km là một công trình kiến trúc cổ. Thành được xây dựng vào năm 1822 triều vua Minh Mạng nhà Nguyễn, tại thủ phủ của trấn Sơn Tây (sau là tỉnh Sơn Tây). Khoảng thập kỷ 70 - 80 của thế kỷ 19,là một trung tâm phòng bị kháng chiến chống Pháp của quan lại triều đình nhà Nguyễn (với những nhân vật lãnh đạo như: Hoàng Kế Viêm, Lưu Vĩnh Phúc,...) giữa hai cuộc xâm lược Bắc kỳ lần thứ nhất và lần thứ hai của Pháp. Thành bị quân Pháp chiếm năm 1884. Năm 1924, Toàn quyền Đông Dương đã ra nghị định để xếp hạng di tích thành cổ này. Tháng 12 năm 1946, một cuộc họp của Chính phủ nước Việt Nam Dân chủ Cộng hòa đã diễn ra ở đây. Ngày nay, tòa thành này vẫn còn tồn tại ở trung tâm thành phố Sơn Tây tỉnh Hà Tây và trở một di tích lịch sử và kiến trúc quân sự. Thành cổ Sơn Tây được kiến trúc theo kiểu Vauban (kiểu công trình quân sự lấy theo tên kỹ sư Vauban người Pháp), tường thành chạy theo đường gãy khúc, nhưng tổng thể có hình vuông, mỗi cạch dài khoảng 400 m, diện tích khoảng 16 ha, chiều cao tường thành khoảng 5 m. Ngoài thành là hào nước sâu 3 m, rộng tới 20 m và dài khoảng 1.795 m, được nối ra sông Tích Giang tại góc thành phía Tây Nam, bốn mặt thành có các cổng vòm bằng đá ong. Thành có 4 cửa quay ra các hướng Bắc (chính xác là hướng Bắc Đông Bắc), Nam, Tây, Đông, và lần lượt có tên là: cửa Hậu, cửa Tiền, cửa Hữu, cửa Tả.\r\n\r\nTrong thành có các hạng mục kiến trúc: cột cờ (tức vọng lâu) cao 18m, vọng cung, điện Kính Thiên, hai ao sen phía trước khu nghi lễ (điện Kính Thiên), gần với cửa Tiền. Cửa Hữu quay ra hướng Tây Tây Bắc là hiện còn nguyên vẹn nhất. Cửa Tả cũng còn tương đối nguyên vẹn, nhưng hai cửa Tiền, Hậu thì bị đổ nát mất (riêng cửa Hậu thì được xây lại mới bằng đá ong nhưng với kỹ thuật xây hiện đại nên đã bị mất tính cổ kính của một di tích). Ngoài ra còn có giếng nước, cột cờ và trại lính. Cùng với chiến lũy Phù Sa, năm 1883, thành cổ Sơn Tây là một căn cứ chống thực dân Pháp xâm chiếm Bắc kỳ.\r\n\r\n', 'thanhcosotay.jpg', 1),
(15, 'Văn miếu Quốc Tử Giám', 'Văn Miếu được xây dựng tháng 10 năm 1070, thờ Khổng Tử, các bậc Hiền triết của Nho giáo và Tư nghiệp Quốc Tử Giám Chu Văn An, người thầy tiêu biểu đạo cao, đức trọng của nền giáo dục Việt Nam. Năm 1076, nhà Quốc Tử Giám được xây kề sau Văn Miếu, ban đầu là nơi học của các hoàng tử, sau mở rộng thu nhận cả các học trò giỏi trong thiên hạ.\r\n\r\nVăn Miếu có tường bao quanh xây bằng gạch. Bên trong có những lớp tường ngăn ra làm 5 khu. Khu thứ nhất bắt đầu với cổng chính, trên cổng có chữ Văn Miếu, dưới cổng là đôi rồng đá mang phong cách thời Lê sơ (thế kỷ 15). Lối đi ở giữa dẫn đến cổng Ðại Trung Môn mở đầu cho khu thứ hai. Hai bên còn hai cổng nhỏ. Vẫn lối đi ấy dẫn đến Khuê Văn Các (gác vẻ đẹp của sao khuê, chủ đề văn học). Hai bên gác cũng có hai cổng nhỏ. Khu thứ ba là từ gác Khuê Văn tới Ðại Thành Môn, ở giữa khu này có một hồ vuông gọi là Thiên Quang Tỉnh (giếng trời trong sáng) có tường bao quanh.\r\n\r\n Hai bên hồ là hai khu vườn bia tức nơi dựng các tấm bia ghi tên những người đỗ tiến sĩ (năm 1993 với sự tài trợ một phần của tổ chức doanh nghiệp Mỹ, Ban Quản lý Văn Miếu đã dựng được tám ngôi nhà che cho các bia này). Tiến sĩ là người đỗ cao nhất trong kỳ thi Ðình. Ngày trước, người đi học sau khoảng 10 năm đèn sách đủ vốn chữ để dự thi Hương tức khoa thi tổ chức liên tỉnh, cứ ba năm mở một khoa. Ðạt điểm cao của kỳ thi này đạt học vị Cử nhân. Năm sau các ông Cử tới kinh đô dự kỳ thi Hội. Những người đủ điểm chuẩn sẽ dự kỳ thi Ðình (thi Hội và thi Ðình thực ra là hai giai đoạn của một cuộc thi). Trúng tuyển kỳ thi này được gọi là Tiến sĩ. Ðỗ Tiến sĩ có thể được bổ làm quan. Hiện có 82 bia, xưa nhất là bia ghi về khoa thi năm 1442, muộn nhất là bia khoa năm 1779. Ðó là những di vật quý nhất của khu di tích. Bước qua cửa Ðại thành là tới khu thứ tư. Một cái sân rộng, hai bên là dãy nhà Tả Vu, Hữu Vu, vốn dựng làm nơi thờ các danh nho. Cuối sân là nhà Ðại bái và Hậu cung, kiến trúc đẹp và hoành tráng. Tại đây có một số hiện vật quý: bên trái có chuông đúc năm 1768, bê', 'vanmieu.jpg', 1),
(16, 'Đồ gỗ Đồng Kỵ', 'Làng Đồng Kỵ - Đi lên từ nghề gỗ\r\nNằm cách Hà Nội khoảng 25 km, thôn Đồng Kỵ - huyện Từ Sơn, tỉnh Bắc Ninh từ lâu vốn nổi tiếng với nghề sản xuất các sản phẩm thủ công mỹ nghệ từ gỗ. Những bộ bàn ghế, tủ đứng, tủ chè....theo các phong cách giả cổ và hiện đại đang có mặt trên khắp cả nước với nhiều nhãn hiệu khác nhau xong đều có xuất xứ từ làng nghề Đồng Kỵ. Cùng với sự phát triển của tỉnh Bắc Ninh trong những năm gần đây thì bộ mặt của Đồng Kỵ cũng có nhiều biến đổi. Giờ đây làng cổ Đồng Kỵ đã được mở mang với quy mô lớn, trực thuộc khu công nghiệp Từ Sơn, tỉnh Bắc Ninh. Phóng viên nhóm THE EYES đã có chuyến thực tế tại Đồng Kỵ để ghi nhận lại những hình ảnh về bộ mặt đổi mới của làng nghề truyền thống này. \r\n\r\nĐây là một trong những nơi tập kết gỗ để phục vụ sản xuất. Tại khu vực này có cả những cây gỗ nguyên khối và cả những sức gỗ đã được cắt xẻ. Theo người dân ở đây thì gỗ trong nước hiện chưa đáp ứng được hết nhu cầu về sản xuất và có những loại gỗ họ buộc phải nhập khẩu từ nước ngoài, trong đó có cả những loại gỗ quý hiếmĐi khắp làng bạn đều có thể bắt gặp những âm thanh rất đều đặn và vui tai. Đó là những tiếng đục, tiếng mài, tiếng các công cụ gỗ va chạm với nhau. Bước vào một xưởng sản xuất, chúng tôi bắt gặp không khí làm việc rất khẩn trương .Từ những xưởng gỗ này sẽ cho ra đời những sản phẩm có tính thẩm mỹ, tính nghệ thuật và mang giá trị cao. Như các bạn đang thấy dưới đây là những bức hoành phi, câu đối, những bức tranh bốn mùa.... Tất cả đều đựơc làm từ gỗ và được khảm trai hoặc sơn son thiếp vàng, tạo nên những sản phẩm hết sức đẹp mắt.và nghiêm túc của những người thợ. Từ những động tác cần tới sức mạnh như cưa, đục đến những công đoạn đòi hỏi sự tỉ mỉ như chạm, khắc đều được những đôi bàn tay khéo léo của người thợ thực hiện một cách thành thục và chính xác.Sau khi khu công nghiệp Từ Sơn đươc thành lập và đi vào hoạt động, quy mô của làng nghề Đồng Kỵ cũng vì thế được mở mang và phát triển mạnh mẽ. Không còn bó hẹp trong những con đường làng cổ', 'dongky.jpg', 4),
(17, 'Chạm bạc Đồng Xâm', 'Làng nghề chạm bạc Ðồng Xâm (tên cũ là Ðường Thâm) nằm ở bên hữu ngạn sông Ðồng Giang. Những ghi chép trong sách sử cho biết: làng này hình thành vào cuối thời Trần-Hồ, cách chúng ta ngày nay trên 600 năm. \r\n\r\nNhưng nghề chạm bạc ở đây thì mãi về sau mới xuất hiện. Làng hiện còn một am thờ và một tấm bia đá ở trong khu chùa Ðường (thôn Thượng Gia ngày nay). Ðó là một văn bia Tổ nghề (dựng năm 1689). Trên văn bia có ghi: \r\n\r\n"Hoàng triều Chính Hoà thập niên, Tổ phụ Nguyễn Kim Lâu (...) Bảo Long tụ lạc học nghệ, đáo Ðồng Xâm xứ kiến lập thập nhị phường kim hoàn truyền nghệ."\r\n\r\nTạm dịch: Năm thứ mười dưới triều vua Chính Hoà (1689), vị tổ sư Nguyễn Kim Lâu vốn làm nghề vàng bạc ở Châu Bảo Long, tới xứ Ðồng Xâm lập ra mười hai phường để truyền nghề. \r\n\r\nNguyễn Kim Lâu sống vào khoảng đầu thế kỷ 17. Và như vậy nghề chạm bạc Ðồng Xâm đã tồn tại gần 400 năm nay. Buổi đầu là nghề hàn đồng, gò thùng chậu, đánh dao kéo, chữa khoá, làm quai và vòi ấm tích, điếu bát...về sau mới làm đồ kim hoàn, chuyên sâu về chạm bạc.Cũng như nhiều nghề thủ công cao cấp khác, như đúc đồng, luyện kim... nghề kim hoàn mang lại thu nhập cao cho người thợ, kỹ thuật lại hết sức phức tạp, nên suốt mấy trăm năm người Ðồng Xâm luôn giữ bí mật nghề. Ðến nay, kỹ thuật này không còn là độc quyền của thợ Ðồng Xâm nữa, nhưng một số thủ pháp kỹ thuật và nghệ thuật tinh xảo nhất vẫn được giữ bí truyền. Phường chạm bạc xưa đã qui định chặt chẽ trong hương ước của làng rằng: người nào đem bí quyết nghề truyền dạy cho nơi khác, cho người làng khác, hay làm đồ giả để lừa người khác, gây sự bất tín thì phải phạt thật nặng...hoặc đem đánh đòn trước nhà thờ Tổ, hoặc phải xoá tên trong phường. \r\n\r\nHàng chạm bạc Ðồng Xâm khác hẳn và nổi trội so với hàng bạc của các nơi khác ở các kiểu thức lạ về hình khối, dáng vẻ sản phẩm, ở các đồ án trang trí tinh vi mà cân đối, lộng lẫy mà nổi rõ chủ đề chính, ở thủ pháp xử lý sáng-tối nhờ tận dụng đặc tính phản quang của chất liệu bạc. Ðặc trưng của sản phẩm Ðồng Xâm là sự điêu', 'dongxam.jpg', 4),
(18, 'Dát vàng quỳ Kiêu Kỵ', 'Tới thăm các di tích lịch sử, văn hoá, các công trình kiến trúc… ta thấy choáng ngợp bởi các pho tượng Phật, bàn thờ, trên các hoành phi, câu đối, trên tranh sơn mài… lấp lánh ánh vàng, ánh bạc, trông thật rực rỡ. Trên các tác phẩm ấy, người ta đã giát những lá vàng quỳ, bạc quỳ. Mặt hàng đặc biệt đó đã được làm ra ở làng Kiêu Kỵ. \r\nLàng nghề truyền thống Kiêu Kỵ là làng nghề truyền thống thuộc đất Đông Ngàn, Kinh Bắc xưa, làng duy nhất ở Việt Nam chuyên làm vàng quỳ, nay thuộc huyện Gia Lâm (Hà Nội), có lịch sử trên 400 năm do danh nhân Nguyễn Quý Trị truyền dạy. Đến Kiêu Kỵ hôm nay, từ xa đã nghe tiếng âm vang khúc nhạc đập quỳ khoan mau, vang qua luỹ tre làng, trải trên các cánh đồng suốt ngày không dứt. Không chỉ riêng tại làng, người Kiêu Kỵ còn đem theo nghề của mình đi khắp các vùng, miền trên đất nước như: Bắc Ninh, Hà Nội, Nam Định, Huế… lập nghiệp. Có lẽ, quê hương Kiêu Kỵ mới có nhiều người mang nghề đi lập nghiệp bốn phương trời như thế. Vào ngày 12 tháng giêng âm lịch, làng vào đám, mở hội nghề khai xuân và lễ cúng tổ nghề. Dân làng từ khắp nơi nô nức về quê tham gia lễ hội. Kiêu Kỵ còn là quê hương của lưỡng quốc tướng quân Nguyên Sơn (tên thật là Vũ Nguyên Bác). Ông tham gia cuộc Vạn lý trường chinh của Bát lộ quân Trung Quốc, từng là thành viên của Chính phủ Lâm thời nước CHNDTH. Đầu thế kỷ 18, dòng họ Vũ Nguyên của ông đã dựng một "xưởng" làm vàng quỳ lớn, được ghi chép trên văn bia hiện còn lưu giữ ở làng. Khôi phục và phát triển nghề xưa Trước Cách mạng tháng 8, nghề làm vàng quý khá phát đạt, cung cấp vàng quỳ cho hầu hết các công trình tín ngưỡng cung đình để giát lên các tượng phật, ngai vàng, hoành phi, câu đối, kiệu rước, cả tranh sơn mài nữa… Trong hai cuộc kháng chiến chống Pháp, Mỹ, nghề làm vàng quỳ gần như bị mai một. dân làng chuyển sang làm nghề sản xuất đồ dùng bằng da và giả da. Sau ngày thống nhất đất nước, nhất là thời kỳ kinh tế mở cửa, các công trình văn hoá, các di tích lịch sử được khôi phục hoặc xây dựng mới rất cần đến vàng q', 'kieuky.jpg', 4),
(19, 'Chiếu Nga Sơn', 'Nhiều người có thể chưa từng đến Nga Sơn,dù chỉ một lần,nhưng từ rất lâu cụ Tố  Hữu đã đưa đến cho nhiều người nét đặc trưng rất riêng của Nga Sơn: Đó là nghề làm chiếu. Nga Sơn có 8 làng nằm dọc bờ biển có nghề dệt chiếu truyền thống lâu đời.Từ xa xưa, chiếu Nga Sơn, Kim Sơn đã được vua chúa Thăng Long biết đến. Trong các cung vua, phủ chúa, trong các bậc danh gia vọng tộc có chiếu Nga Sơn là thêm một bằng chứng thuyết phục cho sự giàu sang, sành điệu của bậc trưởng giả kinh thành. Cái đẹp quyến rũ của chiếu Nga Sơn ấy là sự óng chuốt, mềm mại. Không ở đâu hơn Nga Sơn có sợi cói nhỏ, dài và mềm mại đến thế. Nga Sơn có thể tạo nên nhiều chủng loại chiếu. Tính vượt trội ấy khó có nơi nào bì kịp. Vậy là sản phẩm đặc hiệu làm từ những bàn tay chuyên cần, khéo léo của người dân Nga Sơn được đến với mọi miền. Ngày nay thương hiệu “chiếu Nga Sơn” đã cập bến cảng nhiều quốc gia ưa chuộng chiếu cói. Nhưng cói ngày nay không chỉ tạo ra đặc chủng chiếu. Từ cói Nga Sơn đã tạo nên nhiều sản phẩm hấp dẫn khác: chiếu du lịch hai gấp, ba gấp, bốn gấp tiện lợi, giỏ đựng hoa quả, làn, bình hộp có nắp, đệm... kiểu dáng thanh thoát, trẻ trung. Trong số bạn hàng, Nhật Bản tỏ ra sốt sắng với sản phẩm cói. Từ nhiều năm trước doanh nghiệp Nhật đã đến Nga Sơn, liên hệ chặt chẽ với trên 50 doanh nghiệp cói xuất khẩu của 8 xã ven biển. Giờ đây chiếu cói Nga Sơn đã có hành lang thương mại rộng rãi đến với nhiều quốc gia.  Dệt chiếu bằng đôi tay thủ công, hiển nhiên là truyền thống của nghề này. Đây cũng là dịp để người thợ thủ công Nga Sơn thể hiện hết tài hoa trên từng tấm cói. Nhưng khi cói đã vượt ra khỏi thị trường tỉnh Thanh Hóa để đến với mọi miền, và để vươn xa hơn nữa, thì thủ công e khó đáp ứng kịp. Hơn nữa, đó cũng là một nghề nặng nhọc. Gần đây hai người thợ Nga Liên, một trong 8 làng nghề truyền thống đã mày mò, sáng chế ra chiếc máy dệt chiếu đầu tiên. Chiếu dệt mát đẹp, sợi cói đều tăm tắp, lại nhẹ nhàng hơn nhiều và nhanh. Thay vì 3 giờ người dệt thủ công mới dệt được một chi', 'ngason.jpg', 4),
(20, 'Làng đá Non Nước', 'Vị trí: Làng đá mỹ nghệ Non Nước thuộc phường Hòa Hải, quận Ngũ Hành Sơn, thành phố Đà Nẵng.\r\n\r\nĐặc điểm: Đây là nơi sản xuất đồ mỹ nghệ bằng đá cẩm thạch nổi tiếng khắp trong và ngoài nước.\r\n\r\nCó lẽ không ai đến Ngũ Hành Sơn mà lại không ghé thăm làng đá mỹ nghệ Non Nước. Làng được hình thành vào thế kỷ 18 do nghệ nhân người Thanh Hóa tên là Huỳnh Bá Quát khai phá. Sang thế kỷ 19 thì cả làng đều sinh sống bằng nghề này. \r\n\r\nNguyên liệu để làm ra các sản phẩm mỹ nghệ là đá cẩm thạch trước đây được khai thác ở núi Ngũ Hành Sơn. Đá núi Ngũ Hành Sơn nhiều vân ngũ sắc, vẻ đẹp cao sang, là mặt hàng được ưa chuộng trong xây dựng và kiến trúc. Từ đất đá vô cảm, người nghệ nhân làng đá mỹ nghệ đã thổi vào đó tâm hồn của con người. Quá trình này diễn ra ở nhiều công đoạn, và có cả những công đoạn vất vả, nhọc nhằn vô cùng nhưng niềm hạnh phúc trước một tác phẩm đã thành hình, thái độ ngưỡng mộ của người thưởng thức đã giúp cho người dân làng nghề mỹ nghệ này ngày càng gắn bó với công việc của mình. Nguyên liệu để làm ra các sản phẩm mỹ nghệ là đá cẩm thạch trước đây được khai thác ở núi Ngũ Hành Sơn. Đá núi Ngũ Hành Sơn nhiều vân ngũ sắc, vẻ đẹp cao sang, là mặt hàng được ưa chuộng trong xây dựng và kiến trúc. Từ đất đá vô cảm, người nghệ nhân làng đá mỹ nghệ đã thổi vào đó tâm hồn của con người. Quá trình này diễn ra ở nhiều công đoạn, và có cả những công đoạn vất vả, nhọc nhằn vô cùng nhưng niềm hạnh phúc trước một tác phẩm đã thành hình, thái độ ngưỡng mộ của người thưởng thức đã giúp cho người dân làng nghề mỹ nghệ này ngày càng gắn bó với công việc của mình. \r\n\r\n\r\n', 'nonnuoc.jpg', 4);
INSERT INTO `tainguyendulich` (`matn`, `tentn`, `chitiet`, `anh`, `malh`) VALUES
(21, 'Làng gốm Phù Lãng', 'Làng Phù Lãng (Thuộc xã Phù Lãng, huyện Quế Võ Tỉnh Bắc Ninh), cách thị xã Bắc Ninh khoảng 10km và cách sông Lục đầu khoảng 4km. Phù Lãng nằm bên bờ sông Cầu và có nhiều bến đò ngang suốt ngày chở khách qua lại. Địa địa danh Phù Lãng có thể có từ cuối thời Trần đầu thời Lê. Vào thời kỳ này, Phù Lãng có 3 thôn: Trung thôn, Thượng thôn, Hạ thôn. Phù Lãng được trong và ngoài nước biết đến với nghề gốm truyền thống.\r\n\r\nTheo Tô NGuyễn, Trình Nguyễn trong sách Kinh Bắc-Hà Bắc thì ông tổ nghề gốm Phù Lãng là Lưu Phong Tú. Vào cuối thời Lý, ộng được triều đình cử đi sứ sang Trung Quốc. Trong dịp đi này, ông học được nghề làm gốm và truyền dạy cho người trong nước. Đầu Tiên, nghề này dược truyền vào vùng dâncư đôi bờ sông Lục Đầu sau đó chuyển về vùng Vạn Kiếp (Hải Dương). Vào khoảng đầu thời Trần (thế kỷ XIII) nghề được truyền đến đất Phù Lãng Trung.\r\n\r\nTháng 12/1996, khảo sát bãi gốm cổ ở đầu làng An Trạch, đã phát hiện những mảnh gốm thời Trần, một số lò gốm cổ trên đường từ cuối thôn Phấn Trung sang An Trạch. Điều đí chứng tỏ điều nhận định trước đây cho rằng nghề gốmPhù Lãng có từ thời Trần là có cơ sở.Nguyên liệu để tạo ra những sản phẩm của nghề gốm Phù Lãng là đất sét có mầu hồng nhạt ở làng thống Vát, Cung Kiệm (xã Việt Thống), sau khi mua, đất được trở về Phù Lãng bằng thuyền lớn. Qua nhiều công đoạn, đất sét được luyện dẻo mịn rồi tạo hình trên bàn xoay bằng tay của người thợ thủ công.Sản phẩm chính của gốm Phù Lãng xưa là chum vại, ấm đất, chậu cảnh, tiểu sành...Ngày nay với những bàn tay tài hoa và nhiệt huyết nghề gốm, muốn khôi phục và gìn giữ nghề truyền thống của làng các nghệ nhân thế hệ mới như nghệ nhân Vũ Hữu Nhung - với cái tên quen gọi Gốm Nhung, nghệ nhânThiều với tên quan thuộc Gốm Thiều đã và đang thổi hồn vào cho đất, sáng tạo và phát triển những tinh hoa của nghề gốm với nghệ thuật tạo hình khối, hoa văn, ... các nghệ nhân đã tạo ra được nhiều sản phẩm và mẫu gốm mới như Tranh gốm, lọ hoa gốm, ấm chén gốm, gốm trang trí, gốm gốp tường, lư hương...', 'phulang.jpg', 4),
(22, 'Làng Nghề Sơn Đồng ', 'Cách trung tâm thủ đô khoảng 15km,làng nghề truyền thống Sơn Đồng (Hoài Đức-Hà Nội) được biết đến là nơi giữ gìn và phát triển những tinh hoa văn hóa trong nghệ thuật điêu khắc của nước ta. Trải qua bao thăng trầm sóng gió cùng với lịch sử dân tộc, làng nghề Sơn Đồng cho đến ngày nay vẫn là nơi được lựa chọn đầu tiên khi mọi người cần đến những hoành phi, câu đối, những tượng phật, đồ thờ…\r\n\r\nLàng nghề Sơn Đồng hình thành cùng với sự phát triển của phật giáo Việt Nam. Khi Lý Công Uẩn rời đô từ Hoa Lư (Ninh Bình) ra đất Thăng Long, người dân làng Sơn Đồng đã có nhiều đóng góp trong việc xây dựng kinh thành Thăng Long tráng lệ. Trong thời kì kháng chiến chống Pháp và chống Mĩ, nghề điêu khắc tượng, làm đồ thờ tưởng như mai một dần vì phải tập trung vào sản xuất và chiến đấu. Làng nghề này đứng trước nguy cơ bị mất sau gần 1000 năm tồn tại. Mãi đến những năm tám mươi, cụ Nguyễn Đức Dậu (người làng gọi là cụ Bá Dậu) - một nghệ nhân của làng từ thời Pháp thuộc, đã đứng ra tổ chức các lớp học điêu khắc sơn mài để truyền lại cho con cháu. Và giờ đây, hơn 30 học trò của cụ Bá Dậu trở thành những người thầy, những người chủ xưởng xuất sắc, đang tiếp tục truyền dạy lại cho con em trong làng. Nghệ nhân Nguyễn Viết Thạnh, một trong số các học trò của cụ Bá Dậu và cũng là chủ tịch hội làng nghề Sơn Đồng cho biết: “làng chủ yếu là làm tượng phật và các đồ thờ tự. Trong xã Sơn Đồng có hơn 1500 hộ, hộ nào cũng có ít nhất một người biết chạm khắc gỗ, sơn mài. Làm nghề này không cần “mẫu sinh kề”, vì chúng tôi đã được họa tập từ khi còn “trứng nước”. Phải am hiểu nhà Phật mới làm được tượng có hồn”. Nguyên liệu người làng Sơn Đồng dùng để làm tượng phật hay những đồ thờ tự thường là gỗ mít, gỗ dổi…đặc biệt có gỗ vàng tâm, một loại gỗ quý, nhẹ (có khi để được đến 500 năm nếu bảo quản tốt) cũng là một nguyên liệu người làng hay sử dụng. Những loại gỗ này phải tuân theo đúng quy tắc và chuẩn mực nhất định mới được sử dụng nên chất lượng của những sản phẩm nơi đây luôn được đảm bảo, đọ ', 'sondong.jpg', 4),
(23, 'Làng Lụa Vạn Phúc', 'Là làng nghề dệt lụa tơ tằm nổi tiếng từ xa xưa. Nghề dệt lụa ở Việt Nam có ở nhiều nơi,nhưng không thể không nói tới Vạn Phúc (thị xã hà Ðông, Hà Tây), một vùng dệt lụa thủ công lâu đời và lừng danh của ta. Lụa Vạn Phúc bền đẹp, khoác tấm áo lên người sẽ thấy mềm mại và nhẹ nhàng. Cái nét đắc sắc và độc đáo ấy chính là nhờ vào đôi bàn tay khéo léo, điêu luyện, tinh đời của người dân Vạn Phúc. Trải qua bao thế hệ, lụa Vạn Phúc vẫn luôn giữ được những thủ pháp nghệ thuật truyền thống. Hoa văn bao giờ cũng tranng trí đối xứng, đường nét trang trí không rườm rà, phức tạp mà luôn mềm mại, phóng khoáng, dứt khoát. Bởi vậy, lụa Vạn Phúc không chỉ được ưa chuộng ở trong nước mà đã vượt ra ngoài lãnh thổ Việt Nam tới tay những người sành điệu bốn phương.Tương truyền, bà tổ làng Lụa Vạn Phúc vốn người Hàng Châu (Trung Quốc) theo chồng chinh chiến Bắc Nam, rồi neo lại làng này. Nỗi nhớ quê hương da diết của bà trút hết vào nghề tầm tang, canh cửi nơi dòng Nhuệ giang êm đềm tha thiết. Ngày nối ngày, đời trải đời và nghề dệt trở thành “truyền thống” của làng Vạn Phúc.\r\nĐến Vạn Phúc những ngày này không khỏi mừng vui vì tiếng máy dệt rộn ràng khắp chốn và những cửa hàng bán lụa rực rỡ sắc màu, tấp nập bán mua. Có lẽ, đây là thời điểm “hưng thịnh” của làng Vạn Phúc vì nhà nhà dệt lụa, người người bán lụa với những con phố bán lụa mới mở cứ dài ra mãi. Nhưng không vì thế mà người làng lụa quên thuở “hàn vi”. Chị Q. nhớ lại, ngày xưa gái làng lụa chỉ lo nếu không lấy được chồng làm ruộng thì không có gạo để ăn. Chúng tôi chỉ quen với nhuộm tơ, se sợi, dệt vải nên chẳng thạo nghề nông. Tôi cũng vậy, lấy được ông xã cùng làng nhưng chuyên nghề nông nên cũng yên tâm. Bởi vì thời đó, chúng tôi đi làm cho hợp tác xã, ăn lương theo công điểm, đong gạo theo tiêu chuẩn, chủ yếu là cơm độn, chẳng mấy khi có gạo trắng mà ăn. Hồi đó, hợp tác xã cũng không dệt lụa mà chủ yếu dệt thảm đay, thảm len xuất khẩu sang Liên Xô, Đông Âu. Đến khi không còn hợp tác xã, hết thị trường, người làng lụa ng', 'vanphuc.jpg', 4),
(24, 'Bảo tàng Cách mạng', 'Mở cửa các ngày trong tuần\r\n\r\nBảo tàng Cách mạng Việt Nam có trụ sở tại số 25 phố Tông Đản, quận Hoàn Kiếm, Hà Nội. Đó là một khuôn viên rộng khoảng 1 ha nằm ở trung tâm Thủ đô Hà Nội, giáp ba mặt phố: Trần Quang Khải, Tràng Tiền, Tông Đản. Ngôi nhà Bảo tàng trước năm 1954 là Sở Thương chính Đông Dương do người Pháp xây dựng năm 1917.\r\n\r\nBảo tàng Cách mạng Việt Nam -  Bảo tàng đầu tiên của nước Việt Nam Dân chủ Cộng hoà, chính thức mở cửa đón khách vào thăm quan từ ngày 6 -1-1959. Khi mới ra đời, tổng kho của Bảo tàng có trên 1  vạn hiện vật, hình ảnh, tài liệu văn bản. Giờ đây, con số đó đã tăng lên trên 8 vạn, gồm nhiều sưu tập và bộ sưu tập có giá trị cho việc nghiên cứu khoa học xã hội- nhân văn,  cho việc tuyên truyền, giáo dục truyền thống, bồi dưỡng niềm tự hào dân tộc cho các thế hệ công dân Việt Nam. Các sưu tập hiện vật đó là nền tảng để bảo tàng tổ chức trưng bày về tiến trình phát triển lịch sử của dân tộc Việt Nam thời kỳ cận-hiện đại (từ 1858 đến nay).\r\n\r\nHệ thống trưng bày thường trực của Bảo tàng Cách mạng Việt Nam sử dụng trên 2.500 hiện vật, hình ảnh, tư liệu bày trong 29 phòng với tổng diện tích trên 2.000 m2. Nội dung trưng bày gồm ba phần.\r\n\r\n \r\n\r\nPhần thứ nhất: Thời kỳ đấu tranh giành độc lập của nhân dân Việt Nam từ 1858 đến 1945.\r\n\r\nPhần này được trình bày theo biên niên lịch sử trong 9 phòng đầu tiên của Bảo tàng. Mở đầu là những hiện vật, hình ảnh lịch sử về thực dân Pháp xâm lược và áp đặt bộ máy thống trị của chủ nghĩa thực dân-phong kiến ở Việt Nam. Kết thúc là cuộc Tổng khởi nghĩa của nhân dân Việt Nam dưới sự lãnh đạo của Đảng Cộng sản giành  chính quyền thắng lợi trong toàn quốc và sự ra đời của nước Việt Nam Dân chủ Cộng hoà ngày 2-9-1945.\r\n\r\nTiếp cận với 9 phòng trưng bày đầu tiên, người xem thấy rõ nhiều vấn đề lớn của lịch sử cận đại Việt Nam trong hơn 80 năm: Quá trình xâm lược Việt Nam bằng quân sự của chủ nghĩa Tư bản Pháp; Các phong trào yêu nước chống xâm lược của dân tộc theo các ý thức hệ phong kiến, tư sản, vô sản; Vai trò', 'cachmang.jpg', 5),
(25, 'Bảo tàng dân tộc học', '\r\nLịch sử phát triển\r\n\r\n \r\n\r\n Bảo tàng Dân tộc học Việt Nam vừa là một cơ sở khoa học vừa là một trung tâm văn hóa, có tính khoa học cao và tính xã hội rộng lớn. Vị trí xác định đó đã được thể hiện qua các chức năng của Bảo tàng này: Nghiên cứu khoa học về các dân tộc ở nước ta, sưu tầm, phân loại, đánh giá, bảo quản, phục chế, trưng bày, giới thiệu và khai thác những giá trị lịch sử - văn hóa của các dân tộc, đồng thời cung cấp tư liệu dân tộc học và đào tạo cán bộ cho loại hình bảo tàng Dân tộc học.\r\n\r\n Trong tương lai, Bảo tàng Dân tộc học Việt Nam còn hướng tới giới thiệu, trưng bày cả về văn hóa và văn minh của các dân tộc ở Đông Nam Á và Khu vực.\r\n\r\n \r\n\r\nQuá trình hình thành\r\n\r\n \r\n\r\nLoại hình bảo tàng dân tộc học rất quan trọng và có ý nghĩa to lớn về nhiều phương diện trên qui mô quốc gia cũng như ở từng địa phương, bởi vì nước ta có tới 54 dân tộc. Cho nên, ngay từ năm 1981, Nhà nước đã chủ trương hình thành một Bảo tàng Dân tộc học đặt tại thủ đô Hà Nội. Công trình Bảo tàng Dân tộc học được chính thức phê duyệt luận chứng kinh tế - kỹ thuật ngày 14-12-1987 và được Nhà nước cấp đất để xây dựng: năm 1987- 2.500m2, năm 1988- 9.500m2 và năm 1990 Thủ tướng Chính phủ có quyết định giao toàn bộ 3,27 ha.\r\n\r\n\r\nSuốt nhiều năm, Ban Quản lý công trình này và Phòng Bảo tàng là một bộ phận của Viện Dân tộc học. Ngày 24-10-1995, Thủ tướng Chính phủ ra quyết định về việc thành lập Bảo tàng Dân tộc học Việt Nam (trực thuộc Trung tâm Khoa học Xã hội và Nhân văn Quốc gia).\r\n\r\n \r\n\r\nNgày 12 tháng 11 năm 1997, Bảo tàng khánh thành.Bảo tàng Dân tộc học Việt Nam toạ lạc trên một khu đất rộng ở trên đường Nguyễn Văn Huyên, phường Dịch Vọng, quận Cầu Giấy, cách trung tâm thủ đô chừng 8 km. Đây vốn là vùng đất ruộng của cư dân sở tại; tất cả các công trình cơ sở hạ tầng đều mới được xây dựng cùng với quá trình hình thành Bảo tàng, kể cả đoạn đường lớn dài khoảng 700 m từ đường Hoàng Quốc Việt rẽ vào Bảo tàng (trong tương lai nó sẽ được kéo dài tiếp đến khách sạn Deawoo bên đường Cầu ', 'dantochoc.jpg', 5),
(26, 'Bảo tàng HCM', 'Chủ tịch Hồ Chí Minh - Người anh hùng giải phóng dân tộc, nhà văn hoá kiệt xuất. Người đã hiến dâng trọn đời mình cho sự nghiệp giải phóng dân tộc Việt Nam, góp phần to lớn vào sự nghiệp cách mạng của nhân dân thế giới vì hoà bình, độc lập dân tộc, dân chủ và tiến bộ xã hội. Theo nguyện vọng của toàn thể nhân dân cả nước để tưởng niệm vị lãnh tụ vô cùng kính yêu của mình, Đảng và Nhà nước đã quyết định xây dựng công trình Bảo tàng Hồ Chí Minh.\r\n\r\nCông trình được khánh thành ngày 19/5/1990, tại số 19 - Ngọc Hà – Ba Đình – Hà Nội, đúng vào kỷ niệm 100 nǎm ngày sinh của Chủ tịch Hồ Chí Minh. Đây cũng là thể hiện lòng biết ơn và đời đời ghi nhớ công lao to lớn của Chủ tịch Hồ Chí Minh và quyết tâm của nhân dân Việt Nam kế tục trung thành sự nghiệp cách mạng vĩ đại của Người, đoàn kết phấn đấu xây dựng một nước Việt Nam hoà bình, thống nhất, độc lập, dân chủ và giàu mạnh, hợp tác và hữu nghị với nhân dân thế giới.\r\n\r\nTừ ngày khánh thành Bảo tàng đã đón tiếp hàng triệu lượt khách trong nước và quốc tế đến thǎm quan, nghiên cứu và học tập. Với chức nǎng, nhiệm vụ của một thiết chế vǎn hoá, Bảo tàng Hồ Chí Minh đã trở thành một trung tâm nghiên cứu, giới thiệu về cuộc đời của một vĩ nhân thế kỷ XX, một con người đã cống hiến trọn vẹn cuộc đời cho sự nghiệp giải phóng dân tộc, giải phóng xã hội và giải phóng con người; một con người trọn vẹn trong tư tưởng, đạo đức và phong cách, sống cao thượng và giàu lòng nhân ái. Như Nghị quyết của tổ chức Giáo dục khoa học Liên Hợp Quốc (UNESCO) về kỷ niệm 100 nǎm ngày sinh Chủ tịch Hồ Chí Minh nêu rõ: \r\n\r\n"Chủ tịch Hồ Chí Minh là một biểu tượng kiệt xuất về quyết tâm của cả một dân tộc, đã cống hiến trọn đời mình cho sự nghiệp giải phóng dân tộc vì hoà bình, độc lập dân tộc, dân chủ và tiến bộ xã hội... sự đóng góp quan trọng về nhiều mặt của Chủ tịch Hồ Chí Minh trong các lĩnh vực vǎn hoá, giáo dục và nghệ thuật  là kết tinh của truyền thống vǎn hoá hàng nghìn nǎm của nhân dân Việt Nam và những tư tưởng của Người là hiện thân của nhữn', 'hochminh.jpg', 5),
(27, 'Bảo tàng lịch sử', 'Trong hệ thống các Bảo tàng Quốc gia Việt Nam, Bảo tàng Lịch sử Việt Nam (BTLSVN) được thành lập sớm nhất trên cơ sở kế thừa cơ sở vật chất của Bảo tàng Loui Finô (Louis Finot) - một bảo tàng do Trường Viễn Đông Bác cổ Pháp xây dựng năm 1926, khánh thành năm 1932. Năm 1958, Chính phủ Việt Nam chính thức tiếp quản công trình văn hóa này và xúc tiến nghiên cứu, sưu tầm, bổ sung tài liệu hiện vật, chuyển đổi nội dung từ bảo tàng nghệ thuật thành bảo tàng lịch sử. Ngày 3/9/1958 BTLSVN chính thức mở cửa đón khách tham quan.\r\n\r\n- Hệ thống trưng bày chính của bảo tàng - cuốn sử sống của dân tộc Việt Nam từ thời Tiền sử (cách ngày nay khoảng 30 - 40 vạn năm) đến Cách mạng Tháng Tám - 1945. Với diện tích trưng bày hơn 2.200 m2, gần 7.000 tư liệu hiện vật, hệ thống trưng bày chính của bảo tàng được thể hiện theo nguyên tắc trưng bày niên biểu, lấy sự phong phú của sưu tập hiện vật làm ngôn ngữ biểu đạt chính, kết hợp giữa trưng bày phản ánh giai đoạn và sự kiện lịch sử với trưng bày sưu tập theo hướng trưng bày mở, tạo điều kiện để có thể cập nhật những tư liệu hiện vật mới do công tác nghiên cứu sưu tầm đem lại, làm cho "diện mạo" trưng bày luôn mới mẻ, hấp dẫn người xem. Cùng mục đích ấy, bảo tàng thường xuyên tổ chức các cuộc trưng bày chuyên đề và với hệ thống màn hình ti vi, màn hình cảm ứng hiện đại với hình ảnh phong phú, sống động, những dữ liệu khoa học chân xác ngày càng thỏa mãn nhu cầu khách tham quan, các nhà nghiên cứu khi đến bảo tàng.\r\n - Hệ thống kho cơ sở của bảo tàng hiện lưu giữ hơn 100.000 tiêu bản hiện vật, gồm nhiều chất liệu, nhiều sưu tập hiện vật quý hiếm: Sưu tập hiện vật thuộc các nền văn hóa Núi Đọ, Hòa Bình - Bắc Sơn, Sưu tập văn hóa Đông Sơn, Sưu tập gốm men cổ Việt Nam, Sưu tập điêu khắc đá Chămpa, Sưu tập đồ đồng thời Lê - Nguyễn... Trong những năm vừa qua, kho cơ sở của bảo tàng đã được bổ sung nhiều sưu tập mới có giá trị, đặc biệt là từ khu vực miền Trung, Tây Nguyên, Nam Bộ và ngoài biển Đông từ các con tàu đắm cổ. Hệ thống kho cơ sở được ', 'lichsu.jpg', 5),
(28, 'Bảo tàng Mỹ thuật', 'Trong hệ thống các bảo tàng quốc gia Việt Nam, Bảo tàng Mỹ thuật Việt nam được đánh giá là một trong những bảo tàng có vị trí quan trọng nhất trong việc lưu giữ và phát huy kho tàng di sản văn hoá nghệ thuật của cộng đồng các dân tộc Việt nam. Đến với Bảo tàng, khách tham quan sẽ hiểu được toàn bộ quá trình phát triển của lịch sử mỹ thuật Việt Nam thông qua các sưu tập tài liệu hiện vật đang được trưng bày tại đây.\r\n\r\nTheo Quyết định số 37/ 2004/ QĐ - BVHTT ngày 28/6/2004 của Bộ Văn hoá Thông tin, Bảo tàng Mỹ thuật Việt Nam có những chức năng cơ bản sau đây:\r\n\r\nBảo tàng Mỹ thuật Việt Nam là đơn vị sự nghiệp văn hoá - thông tin có thu, trực thuộc Bộ Văn Hoá - Thông tin, có chức năng bảo quản, trưng bày tài liệu, hiện vật, các tác phẩm mỹ thuật tiêu biểu của Việt Nam.\r\n\r\nBảo tàng Mỹ thuật Việt Nam là bảo tàng quốc gia, có tư cách pháp nhân, có con dấu riêng, được mở tài khoản tại kho bạc nhà nước và ngân hàng.Những nhiệm vụ cụ thể của Bảo tàng cũng được qui định như sau:\r\n\r\n1. Trình Bộ trưởng qui hoạch phát triển, kế hoạch hoạt động dài hạn, hàng năm của Bảo tàng và tổ chức thực hiện sau khi được phê duyệt.\r\n\r\n2. Nghiên cứu về lịch sử mỹ thuật của cộng đồng các dân tộc Việt Nam phù hợp với chức năng, nhiệm vụ được giao thông qua các tài liệu, hiện vật của Bảo tàng.\r\n\r\n3. Sưu tầm kiểm kê, bảo quản, trưng bày và giới thiệu các tài liệu hiện vật về lịch sử mỹ thuật của cộng đồng các dân tộc Việt Nam.\r\n\r\n4. Hướng dẫn, phục vụ nhân dân trong nước và khách nước ngoài tham quan, nghiên cứu tại Bảo tàng; thực hiện các hình thức tuyên truyền giáo dục về lịch sử mỹ thuật cộng đồng các dân tộc Việt Nam thông qua tài liệu hiện vật của Bảo tàng.\r\n\r\n5. Nghiên cứu, ứng dụng tiến bộ khoa học công nghệ vào hoạt động của Bảo tàng\r\n\r\n6. Hướng dẫn chuyên môn nghiệp vụ cho các Bảo tàng, di tích, nhà trưng bày và chủ sở hữu di sản phù hợp với chức năng, nhiệm vụ được giao theo sự phân công của Bộ Văn hoá Thông tin hoặc đề nghị của địa phương, tổ chức và cá nhân.\r\n\r\n7. Tiếp nhận tài liệu, h', 'mythuat.jpg', 5),
(29, 'Bảo tàng phòng không', '   Địa chỉ:  Số 171 và 179 đường Trường Chinh, quận Thanh Xuân, thành phố Hà Nội.\r\nBảo tàng Phòng không - Không quân, thuộc loại hình lịch sử quân sự có nhiệm vụ nghiên cứu, sưu tầm, kiểm kê, trưng bày, tuyên truyền giới thiệu các tài liệu, hiện vật có giá trị lịch sử, văn hoá, khoa học, phản ánh quá trình xây dựng, chiến đấu, trưởng thành của Quân chủng Phòng không - Không quân trong sự nghiệp giải phóng dân tộc, thống nhất Tổ quốc, xây dựng và bảo vệ Tổ quốc.\r\n\r\nBảo tàng Phòng không - Không quân gồm Bảo tàng Phòng không và Không quân sát nhập năm 1999. Bảo tàng Phòng không thành lập ngày 25 tháng 1 năm 1959 , Bảo tàng Không quân thành lập ngày 3 tháng 4 năm 1985,  trực thuộc Cục Chính trị Quân chủng Phòng không - Không quân, xếp hạng 2, theo quyết định xếp hạng Bảo tàng Việt Nam.\r\n\r\nKhái quát nội dung trưng bày: Gồm 7 nội dung trưng bày\r\n\r\n- Phòng khánh tiết.\r\n\r\n- Bộ đội Phòng không - Không quân trong cuộc kháng chiến chống thực dân Pháp:\r\n\r\nTrưng bày những chiến công của Trung đoàn 367 trong chiến dịch Điện Biên Phủ, năm 1954.\r\n\r\n- Sự hình thành và phát triển của lực lượng Phòng không - Không quân chuẩn bị cho cuộc kháng chiến chống Mỹ cứu nước (1954 - 1964).\r\n\r\n- Bộ đội Phòng không - Không quân cùng với quân dân miền Bắc đánh thắng hai cuộc chiến tranh phá hoại của đế quốc Mỹ đối với miền Bắc Việt Nam ( từ tháng 2 năm 1965 đến tháng 1 năm 1973 ).\r\n\r\n- Bộ đội Phòng không - Không quân chiến đấu trong đội hình quân binh chủng hợp thành, tham gia giải phóng miền Nam và làm nhiệm vụ quốc tế, năm 1975.\r\n\r\n- Chiến đấu bảo vệ biên giới phía Bắc, tích cực xây dựng lực lượng sẵn sàng chiến đấu, quản lý vùng trời, cùng cả nước thực hiện thắng lợi hai nhiệm vụ  xây dựng và bảo vệ Tổ quốc Việt Nam xã hội chủ nghĩa ( 1975 đến nay).\r\n\r\n- Trưng bày ngoài trời:   \r\n\r\nTrưng bày sưu tập hiện vật khối lớn, vũ khí trang bị của lực lượng Phòng không - Không quân đã lập công xuất sắc trong hai cuộc kháng chiến như: pháo cao xạ, máy bay, ra đa, tên lửa, xe đặc chủng..., một số vũ khí p', 'phongkhong.jpg', 5),
(30, 'Hồ tây', ' Được sự ưu đãi của đất trời, Hồ tây với các thắng cảnh thiên nhiên, những di tích lịch sử, văn hoá nổi tiếng như Đền Quán Thánh, Chùa Trấn Quốc, Phủ Tây Hồ …tất cả như hoà quyện vào cùng với sự huyền diệu của không gian và cảnh vật xung quanh đã tạo nên một quần thể vô cùng độc đáo.\r\n\r\n \r\n\r\nNhà thuyền Hồ Tây nằm tại một vị trí đắc địa bên bờ Hồ Tây cùng với cảnh quan tuyệt đẹp đã và đang là một địa điểm được lui tới nhiều nhất của du khách trong nước và quốc tế. Được sự ưu đãi của đất trời, Hồ tây với các thắng cảnh thiên nhiên, những di tích lịch sử, văn hoá nổi tiếng như Đền Quán Thánh, Chùa Trấn Quốc, Phủ Tây Hồ …tất cả như hoà quyện vào cùng với sự huyền diệu của không gian và cảnh vật xung quanh đã tạo nên một quần thể vô cùng độc đáo.   \r\n\r\nBằng sự tín nhiệm và lòng yêu mến của khách hàng, Nhà thuyền Hồ Tây đã có vinh dự được tiếp đón và phục vụ các đoàn khách của Chính Phủ và các nước ASEAN cũng như nhiều đoàn khách đặc biệt khác.\r\n\r\nNhà thuyền có một đội ngũ đầu bếp tay nghề cao cùng những thực đơn phong phú với các món ăn Á, Âu, hay Dân tộc 3 miền … sẵn sàng phục vụ bạn tận tình, nhanh chóng với mức giá vô cùng hợp lý. Ngoài ra, Nhà thuyền còn có Dàn nhạc Dân tộc chắc hẳn sẽ làm tăng thêm không khí hấp dẫn cho chuyến du lịch của bạn trên những con tàu Tây Long.\r\n\r\nVới các thuyền có thể chở được từ 20, 50, 200 khách, Nhà thuyền chúng tôi rất thích hợp cho việc tổ chức các hội nghị lớn, nhỏ, đám cưới, sinh nhật hay điểm đến cảu các tour du lịch. Nhà thuyền có chế độ giảm giá, khuyến mại cho các đoàn khách là học sinh, sinh viên.\r\n\r\nĐến với Nhà thuyền Hồ Tây bạn chắc chắn sẽ có được một ấn tượng tốt đẹp về một nới lý tưởng để nghỉ ngơi và hưởng thụ sau những giờ làm việc căng thẳng. Bạn chỉ cần cầm điện thoại và bấm số, chúng tôi sẽ khiến bạn cảm thấy hài lòng.\r\n\r\n \r\n\r\nTổ chức tiệc trên du thuyền Tây Long Du Thuyền Tây Long nhận đặt các loại tiệc như: Dạ tiệc, tiệc buffe, tiệc đám cưới, tiệc ăn hỏi, tiệc mừng đám cưới (kim cương, vàng, bạc ...) tiệc đứng, tiệ', 'ho tay.jpg', 3),
(31, 'Hồ đại lải', 'Là hồ nước rộng 525 ha xen lẫn những cánh rừng xanh thẳm, những bán đảo hoang sơ và các triền đồi bát úp,Đại Lải lâu nay được biết đến là khu nghỉ dưỡng lý tưởng để du khách tìm đến, trải nghiệm và khám phá.Những ngày oi nồng, nóng bức, ai mà chẳng muốn đến vui vẻ nơi không khí mát mẻ, dễ chịu. Chắc chắn, một chuyến tham quan về hồ Đại Lải (xã Phúc Yên, Vĩnh Phúc) sẽ là lựa chọn tốt.\r\n\r\nNằm giữa vùng sơn mạch Tam Đảo, thiên nhiên hào phóng đã ban tặng cho nơi này một hệ thống cảnh quan núi non, sông nước hữu tình. Quanh năm mặt hồ nước luôn gợn sóng trong xanh và đầy ắp. Giữa hồ là một đảo chim rộng tới 4,8 ha, cây cối xanh mướt và um tùm. Vào mùa đông, hàng trăm loài chim từ phương xa về đây tụ hội, tạo nên bức tranh thiên nhiên sống động và hoang sơ. Qua đảo chim, du khách có thể thưởng thức nhiều món ngon đậm chất dân dã như gà đồi, cơm lam, cá nướng… Chiều chiều, du khách có thể thả hồn lắng nghe tiếng chim hót trong lùm cây, ngắm ánh hoàng hôn nơi mặt hồ…\r\n\r\nĐảo chim giữa hồ, nơi bạn có thể tham quan ngắm cảnh và thưởng thức những đặc sản của Đại Lải\r\n\r\nNgoài ra, xung quanh hồ Đại Lải là những thung lũng tự nhiên, các triền đồi bát úp, các eo và nhiều bán đảo đa dạng… Tại đây có nhiều địa điểm cắm trại, nghỉ mát lý tưởng: dãy núi Thằn Lằn hùng vĩ, được xâu chuỗi với hơn 9.000 ha cây rừng phòng hộ để tạo một bức tường thành giữ nước. Dãy Tam Đảo đứng trụ ở phía bắc mặt hồ, như bức bình phong che chắn những luồng gió bấc lạnh lẽo của mùa đông. Nhiệt độ trung bình ở đây tương đối ổn định: mùa hè là 28,9 độ C, mùa đông là 16,8 độ C.\r\n\r\nDu khách thích mạo hiểm, khám phá có thể tổ chức những chuyến đi bộ lên phía Bắc, luồn rừng, vượt đèo Nhe sang đất Thái Nguyên, hoặc rẽ sang núi Mỏ Quạ để leo lên những vách đá dựng đứng cao vút tầng mây. Từ trên đỉnh núi này, có thể chiêm ngưỡng hồ Xạ Hương, hồ Làng Hàm, hồ Gia Khau thấp thoáng trong màu xanh mướt mát của cây rừng. Với du khách thích tham quan, khảo cứu lịch sử, có thể tìm đến với lâu đài thành quách cổ kính của Qu', 'hodailai.jpg', 3),
(32, 'Hồ suối hai', 'Cách Hà Nội khoảng 60 km về phía tây, Suối Hai nằm dưới chân núi Ba Vì được tạo bởi hệ thống đập chính và phụ dài 4 km để giữ nước từ hai suối chính Yên Cư và Cầu Rồng chảy từ trên núi xuống. Đây cũng là nguồn nước tưới cho trên 7.000 ha, dài 7 km, rộng 4 km, lượng nước chứa trong hồ khoảng 45 triệu m3.\r\n\r\nTrong lòng hồ có tới 14 đảo lớn nhỏ, với diện tích khoảng 90 ha. Bạn có thể thăm thú nơi này bằng thuyền nhỏ du ngoạn trên hồ. Trên các đảo và ven hồ, trồng nhiều cây xanh và vườn cây ăn trái. Nhiều loại chim đến đây sinh sống như chim le le, vịt trời, ngỗng trời, mòng két, sâm cầm, giang, sếu... Chúng sinh sống trên mặt nước làm khung cảnh thiên nhiên thêm phong phú.\r\n\r\nVới phong cảnh thoáng, rộng, nước trong, có nhiều bãi tắm đẹp nên địa điểm này đã thu hút được rất đông khách du lịch. Đến với hồ Suối Hai, du khách có thể nghỉ ngơi ở nhà nghỉ của Liên đoàn Lao động Hà Tây với giá cả và dịch vụ phù hợp với người lao động.', 'hosuoihai.jpg', 3),
(33, 'Hồ suối vàng', 'Rời trung tâm Đà Lạt theo hướng Bắc đi Lạc Dương, đến km 7 Tùng Lâm rẽ trái, du khách còn phải vượt qua đoạn đường dài khoảng 12km gập ghềnh uốn lượn giữa những đồi thông chập chùng trước khi đến được hồ Suối Vàng, nơi mà 100 năm trước đây khi lần đầu tiên đặt chân đến vùng đất này, ngẩng ngơ trước cảnh sắc thơ mộng kỳ ảo của thiên nhiên còn nguyên vẹn nét hoang sơ, Yersin đã chạy nhảy reo hò như một cậu học trò nhỏ để sau này đề nghị với toàn quyền P. Doumer cho xây dựng khu nghỉ dưỡng nơi đây.Hồ Suối Vàng gồm hai hồ là Dankia ở trên và Ankroet ở dưới, được tạo bởi hai đập cùng tên Ankroet chắn dòng sông Đa Dung phát nguyên từ núi Langbian; cạnh đó là một thác nước trắng xóa cũng mang tên Ankroet - thác này đã được toàn quyền Decoux chọn làm nơi xây dựng nhà máy thủy điện đầu tiên của Đà Lạt vào năm 1942.\r\nHồ Suối Vàng có sức chứa khoảng 20 triệu khối nước, ngoài việc cung cấp nguồn nước trong lành cho thành phố Đà Lạt, còn được dùng để vận hành tổ máy phát điện của nhà máy thủy điện Ankroet với công suất năm đạt 15 triệu kw/h.\r\nNơi đây còn có nhà máy nước Suối Vàng khá hiện đại do Đan Mạch giúp xây dựng hoàn thành năm 1984 với công suất 18.000m3/giây, với sự kiểm nghiệm thường xuyên của Trung tâm Y tế dự phòng đã xác nhận nước đầu nguồn luôn đạt tiêu chuẩn vệ sinh cần thiết.Dankia - Suối Vàng ngày nay đã được nhìn nhận đúng với giá trị thực của nó. Trong một tương lai không xa nơi đây cùng với khu vực Tuyền Lâm sẽ là một đối trọng của Đà Lạt hiện nay đã bị bê tông hóa tràn lan đến bực bội. Một liên doanh gồm một bên là Tỉnh Lâm Đồng và một bên là Singapore đã có kế hoạch đánh thức nàng sơn nữ Dankia với hàng trăm hạng mục lớn nhỏ sẽ được dựng lên bên hồ, trên hồ và giữa những đồi thông, thảm cỏ mượt mà … chúng ta có quyền hy vọng nơi đây sẽ đem lại sức hấp dẫn quyến rũ cho một vùng đất huyền thoại luôn làm say đắm biết bao khách viễn du\r\n \r\n', 'hosuoivang.jpg', 3),
(34, 'Mai châu', 'Nằm ở độ cao từ 200-1.500m so với mực nước biển nên quanh năm Mai Châu có khí hậu mát mẻ, trong lành. Ở Mai Châu, ngoài hai dân tộc Thái trắng và Mường còn có 5 dân tộc khác cùng sinh sống.\r\n\r\nDo được thiên nhiên ban tặng vẻ đẹp hùng vĩ hiếm có, hệ thống giao thông tương đối thuận lợi nên những năm gần đây, Mai Châu phát triển rất mạnh loại hình du lịch sinh thái và văn hóa. Điều đặc biệt là ở đây ai cũng biết làm du lịch, nhà nhà làm du lịch… với tất cả những gì họ vốn có.\r\n\r\nĐặt chân đến Mai Châu, điều đầu tiên du khách nhìn thấy là những nếp nhà sàn xinh xắn nằm nép mình dưới thung lũng xanh tươi, bảng lảng trong làn sương núi mờ ảo. Nơi đây được mệnh danh là “phố trong sương” đây có nhiều thắng cảnh hấp dẫn du khách nhất của Mai Châu tập trung chủ yếu ở bản Lác, Pom Coọng (xã Chiềng Châu) với một quần thể nhà sàn xưa rất đẹp và kiên cố bậc nhất. Nhà sàn vừa là nơi bà con các dân tộc đón khách đến tham quan, nghỉ ngơi, cũng là những sân khấu biểu diễn văn nghệ truyền thống và cũng là những “shop mini” giới thiệu các sản phẩm đặc trưng do chính bàn tay họ làm ra. Nhà sàn ở bản Lác, Pom Coọng được xây dựng theo một mô típ chung: chiều cao sàn nhà cách mặt đất khoảng 1,5m, làm bằng những cột gỗ chắc chắn; sàn nhà làm bằng bương, tre; nóc nhà lợp lá gồi, lá mây hoặc ngói. Nhà nào cũng có vài khung cửa sổ to, rộng mở để đón gió trời. Chung quanh nhà treo rất nhiều dò hoa lan hoa rừng đủ màu rực rỡ hay những món quà lưu niệm mang đậm bản sắc dân tộc như một cung tên, một cây sáo, một tấm khăn thổ cẩm hay giỏ bắt cua…\r\n\r\nĐặc biệt, hầu như gia đình nào cũng có một khung cửi để dệt vải kê ngay dưới gầm sàn. Những khung cửi rất đơn sơ, mộc mạc và cũ kỹ nhưng đây chính là công cụ để những bàn tay khéo léo của các cô gái Thái dệt nên những tấm áo, tấm khăn thổ cẩm rực rỡ sắc màu. Bao quanh mỗi nếp nhà sàn là những hàng cau thẳng tắp, những vuông ao nhỏ bốn mùa các loài cá tung tăng bơi lội, tô điểm thêm cho cuộc sống sinh động nơi đây.\r\n\r\nĐứng bên khung cửi xem những cô gái ', 'maichau.jpg', 3),
(35, 'Phong Nha', 'Liên hiệp quốc đã tuyên bố năm 2010 là Năm Quốc tế Đa dạng sinh học, nhằm khẳng định đa dạng sinh học là sự đa dạng của cuộc sống, là tài sản vô hình và hữu hình của cuộc sống nhân loại.  Điều này cũng cảnh báo rằng đa dạng sinh học đang bị suy giảm với tốc độ báo động do quản lý kém và thiếu nhận thức về tầm quan trọng của bảo tồn đa dạng sinh học đối với sự tồn tại của xã hội cũng như sự thịnh vượng lâu dài của nhân loại. Chủ đề Ngày Quốc tế Đa dạng sinh học 2010 là “Đa dạng sinh học, Phát triển và Giảm nghèo” nhằm kỷ niệm sự sống của hành tinh, nhân loại và đa dạng sinh học. Năm nay Tổ chức Môi trường thế giới đưa ra thông điệp " Nhiều loài - Một Hành tinh - Tương lai chúng ta" nhân ngày Môi trường Thế giới nhằm nhấn mạnh đa dạng loài ngày càng suy giảm, áp lực của con người quá lớn dẫn đến nguy cơ tuyệt chủng cao, không gian sống của các loài ngày càng bó hẹp.\r\n\r\nTrong nhiều năm qua, nhận thấy vấn đề môi trường và đa dạng sinh học ảnh hưởng lớn đến kinh tế, xã hội, đặc biệt vì sự phát triển bền vững trong tương lại, Chính phủ Việt Nam đã xúc tiến các chương trình bảo tồn, bảo vệ tài nguyên, cam kết thực hiện Công ước quốc tế về Đa dạng sinh học... Năm nay, Quảng Bình được chọn đăng cai tổ chức  Ngày Môi trường Thế giới và Ngày Đa dạng sinh học 2010 với chủ đề "Đa dạng sinh học là sự sống, đa dạng sinh học là cuộc sống của chúng ta".  Hưởng ứng Ngày Môi trường thế giới và Ngày Quốc tế đa dạng sinh học 2010, Ban quản lý Vườn quốc gia Phong Nha - Kẻ Bàng được sự hỗ trợ của Tổ chức Hợp tác Kỹ thuật Đức (GTZ) tiến hành một loạt các hoạt động nhằm nâng cao cộng đồng, cán bộ, thanh niên và học sinh tầm quan trọng và ý nghĩa của môi trường và đa dạng sinh học đối với con người.\r\n\r\nNgày 2/6 tại trụ sở Ban quản lý Vườn quốc gia diễn ra buổi mít tinh với chủ đề " Hành động vì đa dạng sinh học"  và  Lễ ký cam kết bảo vệ môi trường giữa Ban quản lý VQG Phong Nha - Kẻ Bàng, Chính quyền các xã vùng đệm, các trường học và các tổ chức tài trợ với sự tham dự của đông đảo cộng đồn', 'phongnha.jpg', 3),
(36, 'Rừng Cúc Phương', 'Ðến Cúc Phương đẹp nhất vào mùa khô từ tháng 12 đến tháng 4,khi đã qua đi những cơn mưa rừng dữ dội.Rừng nguyên sinh Cúc Phương nằm sát vùng đồng bằng Bắc bộ, nhất là nằm trên tuyến đường du lịch với những điểm du lịch hấp dẫn như Bích Ðộng, cố đô Hoa Lư, bãi biển Sầm Sơn... nên thuận lợi đối với khách du lịch trong và ngoài nước.\r\n\r\nTuy nằm ở rìa đồng bằng nhưng do địa hình núi đá vôi khá hiểm trở nên rừng Cúc Phương mới được phát hiện vào năm 1960. Ðến 1966, Cúc Phương chính thức trở thành vườn quốc gia đầu tiên của Việt Nam.\r\n\r\nVườn quốc gia Cúc Phương được bao bọc bởi các núi đá vôi có độ cao trung bình 300 - 400m. Ðỉnh núi cao nhất là đỉnh Mây Bạc, cao 656m. Ðến Cúc Phương, bạn được ngắm một khung cảnh thiên nhiên kỳ bí, hùng vĩ giữa rừng bướm rập rờn, tận mắt chứng kiến nhiều loại cây hàng ngàn năm tuổi như chò, chò chỉ, sấu... và những loài động vật quý hiếm như voọc quần trắng, sóc bay cùng với những đàn hươu sao, nai đã thuần dưỡng... Ðộng thực vật rừng Cúc Phương rất phong phú. Hệ thực vật có gần 2.000 loài khác nhau, rừng đã phát triển đến giai đoạn cực đỉnh với cấu trúc 5 tầng. Chỉ riêng loài động vật có xương sống đã có tới 450 loài, chiếm 38% số loài động vật trong cả nước.\r\n\r\nChính vì địa hình núi đá vôi nên rừng Cúc Phương có nhiều hang động đặc thù. Ðộng Trăng Khuyết, động Con Moong, động Phò Mã, động Người Xưa... mỗi động có vẻ đẹp kỳ thú riêng. Hãy tới Quèn Voi, tương truyền đây là nơi xưa kia quân của người anh hùng áo vải Nguyễn Huệ đã tập kết trước khi thần tốc tiến về Thăng Long đại phá quân Thanh mùa xuân 1789.\r\n\r\nCác chương trình môi trường toàn cầu, chương trình phát triển của Liên Hiệp quốc, tổ chức quốc tế về khu hệ thực vật FFI... đã từng nghiên cứu về nét nguyên sinh điển hình của rừng nhiệt đới mưa ẩm gió mùa Đông Nam Á tại rừng Cúc Phương.', 'rungcucphuong.jpg', 3),
(37, 'Bà Chúa Kho', 'Hàng năm cứ từ mồng 10 tháng Giêng âm lịch trở ra, khách thập phương lại lũ lượt đổ về đền Bà Chúa Kho ở Bắc Ninh lễ bái cầu tài cầu lộc. Việc cầu khấn ai cũng giống ai nhưng nguồn gốc, thân thế, sự nghiệp Bà Chúa Kho thì mỗi người hiểu mỗi khác.\r\nTheo cuốn sách của tác giả Khánh Duyên, Sở VH - TT Hà Bắc ấn hành trước đây thì “Tên thật của bà chả ai rõ được (…) bà vốn xuất thân trong một gia đình nghèo khổ ở Thôn Cổ Mễ (…). Nhà vua mời bà về cung lấy làm vợ…”. Sau đó vì nhớ nhà, vua cho về lập đồn điền, lại giao cho giữ kho lương thực bên sông Cầu ở phòng tuyến sông Như Nguyệt… Một cuốn sách khác có tên Lịch sử đền Bà Chúa Kho của hai tác giả Nguyễn Xuân Cẩn, Nguyễn Huy Hạnh nội dung cũng gần giống quyển trên. Ít lâu sau chân dung bà lại được tác giả Phan Huy Đông tái hiện trong hai tiểu thuyết Huyền thoại Bà Chúa Kho và Huyền thoại Bà Chúa Lẫm in chung một quyển do nhà xuất bản Văn hoá – Dân tộc phát hành đầu năm 1998. Ở “Lời nói đầu” tác giả đưa ra quan điểm “Sự tích Bà chúa Kho chỉ là những huyền thoại dân gian mà thôi (…) vấn đề tín ngưỡng đó thuộc chức năng các nhà nghiên cứu, các nhà chức trách, các nhà văn hoá…”. Nhân vật Bà Chúa Kho trong Huyện thoại Bà Chúa Lẫm  cũng mờ nhạt như mấy quyển kia, ngược lại trong Huyền Thoại Bà Chúa Kho thì thân phận nhân vật lại được miêu tả khá cặn kẽ như “bà là Lý Chiêu Phong, Công chúa thứ sáu của Vua Lý Thánh Tông, sau gả cho Hoàng Lục là quan trưởng ở Quảng Nguyên, bà được vua cha giao cho trông coi lương tiền quốc khố của triều đình…”. Rồi khi biết ý đồ nhà Tống đánh ta, vua liền “Chỉ thị cho công chúa  (…) đặt một kho lương thảo, quốc khố ở mạn Kinh Bắc…”. Lại có một tài liệu khác tỉ mỉ hơn “Bà là Lý Chiêu Phong, con gái vua Lý Thánh Tông và hoàng hậu Thượng Dương, sinh ngày 12 tháng tư năm Giáp Ngọ - 1054 (…) làm chủ kho binh lương (Không nói đến tiền, vàng – TX) ở núi Cổ Mễ Tức Vũ Ninh. Bà tham gia chiến đấu chống quân Tống và anh dũng hi sinh ngày 12 tháng giêng năm Đinh Tý, tức 16-2-1077 tại mặt trận sông Như Nguyệt', 'bachuakho.jpg', 2),
(38, 'Chùa Bãi Đính', ' Dẫu trời rét buốt, nhưng trên đỉnh núi sương mờ bảng lảng, hàng trăm thợ mộc, thợ xây vẫn miệt mài với những khúc gỗ to bằng 2-3 vòng tay người ôm, với hàng trăm pho tượng đá cao hơn đầu người, đánh đu thân mình trên những giàn giáo chằng chịt ở lưng chừng trời… để mong sớm có một ngôi chùa được coi là to và đẹp nhất Việt Nam.\r\nĐó chính là chùa Bái Đính, nằm trong một thung lũng mênh mang hồ và núi đá, thuộc xã Gia Sinh, huyện Gia Viễn (Ninh Bình).\r\n\r\nNhững kỷ lục\r\n\r\nTừ cố đô Hoa Lư (Ninh Bình) đi vào đến núi Bái Đính chỉ khoảng 20km. Cách xa 2-3km đã thấy 2 ngôi chùa như hai búp sen khổng lồ mọc vững chãi trên vách núi, nối tiếp nhau, với những mái đao cong vút, xanh biếc. Tòa nằm phía trước, bên dưới gọi là Pháp Chủ điện. Tòa nằm phía trên, cách khoảng 100m, gọi là Tam Thế điện.\r\n\r\nPhía ngoài cùng, ngay con đường quanh co dẫn lên chùa, cách Pháp Chủ điện khoảng 300m là gác chuông 3 tầng, 24 mái. Đây là nơi sẽ đặt một quả chuông nặng tới 36 tấn. Hiện tại, hai hạng mục gồm cổng tam quan (chạy dài hơn 200m) và hai hành lang tượng La Hán (nằm ở hai bên, dẫn từ cổng tam quan lên gần sát Tam Thế điện với chiều dài khoảng 500m, mỗi bên đặt 250 tượng La Hán bằng đá trắng) mới bắt đầu được động thổ.\r\n\r\nCả một quần thể chùa nằm trên núi Bái Đính, nhìn ra hồ Đầm Thị ở phía Bắc và xa hơn nữa là sông Hoàng Long. Đứng ở sân chùa Bái Đính trông ra, bốn bề là cảnh sông nước và núi đá vôi rất hữu tình, mang nét đặc trưng hiếm có của vùng Gia Viễn (Ninh Bình)- vốn được mệnh danh là “vịnh Hạ Long trên cạn”.\r\n\r\nMặc dù đến nay, ngôi chùa vẫn chưa xây dựng xong, mới hoàn tất được khoảng 70% công việc, xây dựng chùa, nếu tính cả dự án gồm nhiều hạng mục như hồ, suối, khu vui chơi giải trí, hang động, đường sá… thì mới chỉ đạt 30%, nhưng danh tiếng của nó đã lan rộng ra khắp các vùng vì những kỷ lục đáng ngạc nhiên mà từ xưa đến nay chưa từng gặp ở Việt Nam.\r\n\r\nKỷ lục đầu tiên chính là sự bề thế và hoành tráng của ngôi chùa. Ngôi Tam Thế điện có diện tích lên tới 2.400m2, gồm 12 mái, vớ', 'chuabaidinh.jpg', 2),
(39, 'Chùa Hoàng Pháp', ' \r\nChùa Hoằng Pháp không chỉ đẹp về cảnh quan mà còn đẹp về đạo hạnh của vị Tổ khai sơn. Chùa tọa lạc trên khu đất diện tích 06 ha, tại Thành Ông Năm, xã Tân Hiệp, huyện Hóc Môn, thành phố Hồ Chí Minh.\r\n \r\nChùa Hoằng Pháp do cố Hòa thượng Ngộ Chân Tử sáng lập năm 1957. Nơi đây xưa kia là một cánh rừng chồi. Sau hai năm khai phá, năm 1959 Hòa thượng mới bắt đầu xây dựng bằng gạch đinh, hai tầng mái ngói, mặt chùa xoay về hướng Tây Bắc. Do đức độ cao dày của Hòa Thượng, Phật tử các nơi quy tụ về quy y ngày một nhiều. Để có đủ chỗ lễ bái, giảng đạo, năm 1971, Hòa thượng xây nối thêm mặt tiền chánh điện dài 28m, tường xây bằng gạch block, mái lợp tole cement.\r\n \r\nNgoài việc tu hành, tiếp Tăng độ chúng, hoằng truyền Phật pháp, xây dựng ngôi Tam Bảo, Hòa thượng luôn quan tâm đến người hoạn nạn. Năm 1965, trước cảnh màn trời chiếu đất của đồng bào bị chiến tranh tàn phá tại Đồng Xoài, Thuận Lợi, Hòa thượng đã đón nhận 60 gia đình gồm 261 nhân khẩu về chùa nuôi dưỡng trong 8 tháng, sau đó mua đất xây cất 55 căn nhà cho đồng bào định cư.\r\n \r\nNăm 1968, do chiến tranh những trẻ thơ mất cha lạc mẹ không nơi nương tựa hoặc nghèo đói thất học ngày càng nhiều, Hòa thượng lại thành lập viện Dục Anh, tiếp nhận 365 em từ 06 đến 10 tuổi về nuôi dạy. Năm 1974, với dự định mở làng cô nhi tiếp nhận thêm hàng ngàn trẻ thơ bất hạnh và thành lập đền thờ Quốc Tổ Hùng Vương, Hòa hượng đã mua 45 mẫu đất tại ấp Phú Đức, xã Tân Tạo, huyện Bình Chánh. Công việc khai hoang đắp đường đang tiến hành thì tháng 4/1975 miền Nam được giải phóng, đất nước thống nhất, Hòa thượng đã hiến số đất đó cho Ban Quản trị khu Kinh tế mới Lê Minh Xuân.\r\n \r\nSau khi đất nước thống nhất, số trẻ em được thân nhân nhận về, viện Dục Anh giải tán, Hòa thượng lại tiếp tục hạnh nguyện từ bi cứu khổ nhận nuôi dưỡng các cụ già neo đơn hoặc vì gia cảnh khó khăn. Năm 1988, Hòa thượng an nhiên thị tịch lúc 13g30 ngày 16 tháng 10 năm Mậu Thìn tại chùa Hoằng Pháp, trụ thế 88 năm, 65 tuổi đạo.\r\n \r\nKế tục sự nghiệp tại chùa Hoằng Ph', 'chuahoangphap.jpg', 2);
INSERT INTO `tainguyendulich` (`matn`, `tentn`, `chitiet`, `anh`, `malh`) VALUES
(40, 'Chùa Phật Tích', 'Tương truyền rằng xưa kia núi và chùa Phật Tích bạt ngàn hoa Mẫu đơn. Huyền thoại "Từ Thức gặp Tiên" cũng ra đời tại chùa Phật Tích: Nàng tiên Giáng Hương du xuân xuống trần đến ngắm hoa Mẫu đơn ở chùa, chẳng may nàng vô ý vịn gãy một cành Mẫu đơn và bị các chú tiểu trong chùa bắt vạ. Từ Thức đã cởi áo đền cho nhà chùa để xin tha cho nàng. Chàng trai hào hoa và nàng Giáng Hương xinh đẹp cảm mến rồi thương nhau. Họ thường hẹn gặp nhau và đến một ngày nàng đã mời chàng về chơi nhà, lúc này chàng mới biết nàng là tiên nữ và chàng đã lạc vào cõi tiên…\r\nVào những ngày lễ hội bây giờ, hoa Mẫu đơn không còn nhiều được như xưa, song dọc đường dẫn lên đỉnh núi Phật Tích (Tiên Du) vẫn thắm đỏ những bụi hoa Mẫu đơn. Khách đến với Phật Tích không chỉ được ngắm hoa mà còn được thưởng ngoạn các trò chơi dân gian, được nghe đọc thơ, bình thơ và nghe hát quan họ… Không khí thanh bình tĩnh lặng thường ngày của Phật Tích như bị vỡ ra bởi cái náo nhiệt của hội xuân, nhưng sự linh thiêng thì vẫn nguyên vẹn, thể hiện qua nét mặt thành kính của những thiện nam tín nữ.\r\nTượng Phật A Di Đà bằng đá xanh- biểu tượng cho lòng từ bi- ngự trong gian chính của Chùa mang những nét độc đáo của trường phái nghệ thuật Ấn Độ. Chiếc đầu rồng bằng đá dưới đáy giếng cổ trước cổng chùa cũng như hai hàng thú cổ: Sư tử, voi, tê giác, trâu, ngựa bằng đá tạc ở tư thế quỳ đã tạo thêm vẻ linh thiêng cổ xưa cho ngôi chùa. Dân trong vùng rất tự hào vì trong các Chùa ở vùng Kinh Bắc chỉ duy nhất chùa Phật Tích là còn dấu tích của những hàng thú cổ bằng đá. Với chiếc giếng cổ trước cổng chùa cũng vậy, nhiều người quanh vùng vẫn giữ thói quen đến xin nước rất trong của nó để dùng. Họ quan niệm dùng nước ở đây sẽ luôn gặp những điều may mắn.\r\nTheo sử sách thì từ thời nhà Lý, năm 1041 vua Lý Thánh Tông đã cho xây Viện Từ Thị Thiên Phúc trên núi Phật Tích. Năm 1057 Vua cho xây một tháp cao 42 mét, bên trong tháp đặt pho tượng A Di Đà, đồng thời xây chùa Vạn Phúc Tự. Đến đời Trần tháp bị đổ, lộ ra pho tượng, dân chúng ', 'chuaphattich.jpg', 2),
(41, 'Chùa Thiên Mụ', 'Lịch sử\r\n\r\nTrước thời điểm khởi lập chùa, trên đồi Hà Khê có ngôi chùa cũng mang tên Thiên Mỗ hoặc Thiên Mẫu, là một ngôi chùa của người Chăm1.\r\n\r\nTruyền thuyết kể rằng, khi chúa Nguyễn Hoàng vào làm Trấn thủ xứ Thuận Hóa kiêm trấn thủ Quảng Nam, ông đã đích thân đi xem xét địa thế ở đây nhằm chuẩn bị cho mưu đồ mở mang cơ nghiệp, xây dựng giang sơn cho dòng họ Nguyễn sau này. Trong một lần rong ruổi vó ngựa dọc bờ sông Hương ngược lên phía đầu nguồn, ông bắt gặp một ngọn đồi nhỏ nhô lên bên dòng nước trong xanh uốn khúc, thế đất như hình một con rồng đang quay đầu nhìn lại, ngọn đồi này có tên là đồi Hà Khê.\r\n\r\nNgười dân địa phương cho biết, nơi đây ban đêm thường có một bà lão mặc áo đỏ quần lục xuất hiện trên đồi, nói với mọi người: "Rồi đây sẽ có một vị chân chúa đến lập chùa để tụ linh khí, làm bền long mạch, cho nước Nam hùng mạnh". Vì thế, nơi đây còn được gọi là Thiên Mụ Sơn.\r\n\r\nTư tưởng lớn của chúa Nguyễn Hoàng dường như cùng bắt nhịp được với ý nguyện của dân chúng. Nguyễn Hoàng cả mừng, vào năm 1601 đã cho dựng một ngôi chùa trên đồi, ngoảnh mặt ra sông Hương, đặt tên là "Thiên Mụ".\r\nTên gọi\r\nDựa theo huyền thoại, đồng thời căn cứ hình dạng Hán tự từng ghi trên bao tài liệu cấu tạo bằng nhiều chất liệu, đủ khẳng định rằng trong tên Thiên Mụ, ngữ tố "Thiên" có nghĩa là "trời".\r\nNăm 1862, dưới thời vua Tự Đức, để cầu mong có con nối dõi, nhà vua sợ chữ "Thiên" phạm đến Trời nên cho đổi từ "Thiên Mụ" thành "Linh Mụ" (hay "Bà mụ linh thiêng").\r\n\r\nVấn đề kiêng cữ như đã nêu chỉ diễn tiến từ năm Nhâm Tuất (1862) cho tới năm Kỷ Tỵ (1869). Sau đó, người dân thoải mái gọi hai tên: chùa Thiên Mụ và chùa Linh Mụ.\r\n\r\nVì rằng từ "Linh" đồng nghĩa với "Thiêng", âm người Huế khi nói "Thiên" nghe tựa "Thiêng" nên khi người Huế nói "Linh Mụ", "Thiên Mụ" hay "Thiêng Mụ" thì người nghe đều hiểu là muốn nhắc đến chùa này.\r\n\r\nMột số người còn đặt tên cho chùa là Tiên Mụ (hay "Bà mụ thần tiên"). Cách gọi này không được giới nghiên cứu chấp nhận.\r\n\r\nKiến trúc\r\n\r\nChùa Thiên Mụ ', 'chuathienmu.jpg', 2),
(42, 'Đền Trần ', 'Lễ hội diễn ra từ ngày mồng 10 đến 20, tháng 8 âm lịch. Đây là Lễ hội có quy mô lớn được tổ chức hàng năm, không chỉ đơn thuần mang yếu tố tín ngưỡng thuần tuý mà còn là hình thức sinh hoạt văn hoá tinh thần mang đậm đạo lý truyền thống "uống nước nhớ nguồn", tưởng nhớ công lao anh hùng dân tộc Quốc công tiết chế Hưng Đạo đại vương Trần Quốc Tuấn (còn gọi là Đức Thánh Trần)- người đã có công xây dựng, bảo vệ đất nước.\r\n \r\nHưng Đạo Đại Vương Trần Quốc Tuấn là một nhà quân sự thiên tài, một nhà chính trị xuất sắc và một nhà văn hóa lớn. Với vai trò Quốc công Tiết chế, ông đã thống lĩnh toàn bộ lực lượng 3 lần chiến thắng giặc Nguyên-Mông xâm lược nước ta (năm 1258, năm1285 và năm 1287). Ông mất năm 1300 (ngày 20 tháng 8 Âm lịch). Trong các vị anh hùng dân tộc ở nước ta vào thời phong kiến, hiếm có nhân vật nào được quảng đại quần chúng nhân dân tự nguyện phụng thờ nhiều như Quốc công Tiết chế Hưng Đạo đại vương Trần Quốc Tuấn. Theo số liệu thống kê của Ban quản lý di tích Nam Định, trên địa bàn toàn tỉnh có tới 195 di tích thờ Đức Thánh Trần.\r\nMỗi năm Lễ hội Trần càng thu hút đông đảo du khách thập phương và các tầng lớp nhân dân trong tỉnh. Năm nay, từ đầu tháng 8 du khách từ nhiều tỉnh thành phố trong cả nước đã nô nức đổ về quần thể di tích Đền Trần, Đền Bảo Lộc thuộc TP Nam Định và huyện Mỹ Lộc để trẩy hội. Để Lễ hội diễn ra trang trọng, mang ý nghĩa giáo dục truyền thống yêu nước, UBND tỉnh Nam Định đã thành lập Ban Chỉ đạo, phân công trách nhiệm cho từng đơn vị, địa phương liên quan. Cùng với đẩy mạnh tuyên truyền tạo khí thế vui tươi cho nhân dân và khách thập phương về dự Lễ hội, các ngành chức năng tăng cường công tác thanh tra, kiểm tra, xử lý nghiêm những vi\r\nphạm về hoạt động văn hoá, giữ gìn vệ sinh môi trường xanh, sạch, đẹp...\r\nĐỉnh hương bằng đá\r\nNgày 30/9 (tức 20/8 Âm lịch) tại Khu di tích lịch sử - văn hoá Trần thuộc phường Lộc Vượng và đền Bảo Lộc thuộc xã Mỹ Phúc, thành phố Nam Định và huyện Mỹ Lộc sẽ tổ chức lễ dâng hương, mít tinh trọng thể kỷ ni', 'den tran.jpg', 2),
(43, 'Đền Bạch Mã', 'Đền nằm ở địa chỉ 76 phố Hàng Buồm, phường Hàng Buồm. Xưa, nơi đền toạ lạc thuộc địa dư phường Hà Khẩu, tổng Đông Thọ, huyện Thọ Xương, phủ Hoài Đức. Di tích được coi là một trong những danh lam thắng cảnh của “Thăng Long tứ trấn”.\r\n\r\nĐền Bạch Mã thờ thần “Long Đồ Thần Quân Quảng Lợi Bạch Mã Đại Vương”. Tương truyền rằng, vào thế kỷ IX, viên quan đô hộ nhà Đường (Trung Quốc ) là Cao Biền đắp La Thành. Khi ra ngoài Cửa Đông, thấy một người lạ trong đám mây ngũ sắc. Biền vốn là đạo sỹ có ý muốn trấn áp. Đêm, Biền nằm mộng thấy người đã gặp. Người đó tự xưng là Long Đỗ. Cao Biền đem chiếc búa bằng đồng đi chôn yểm. Đêm sau nổi mưa gió. Sáng ra thấy chiếc búa đồng bị đánh tan như cát bụi. Biền sợ và lập đền thờ. Năm 1010, khi Lý Thái Tổ dời đô từ Hoa Lư ra Thăng Long, định đắp thành, nhưng nhiều lần thành đắp lên lại bị sụp đổ. Vua cho người cầu khẩn ở đền thờ thần Long Đỗ thì thấy một con ngựa trắng từ trong đền đi ra. Vua lần theo dấu vét chân ngựa để vẽ đồ án xây thành, thành mới đứng vững. Thần được Lý Thái Tổ phong làm Thành hoàng của kinh thành Thăng Long. Thần Long Đỗ, tức Thần núi “rốn Rồng” cũng gọi là núi Nùng (hiện nay nằm trong Thành cổ Hà Nội). Tương truyền núi có khe thông sâu xuống đất, tiếp nhận khí thiêng sông núi. Văn bia “Trùng tu Bạch Mã miếu bi ký” có niên đại Minh Mệnh nguyên niên (1820), hiện còn tại đền cho biết: “Ngài là vị Thành hoàng của kinh thành Thăng Long… Thần một thôn, một ấp đều được tôn kính, huống đây là vị thần chủ tể một khu vực ngàn dặm, được hàng trăm đời vua cúng tế. Các công lao ban phúc cho đất nước, giúp đỡ cho nhân dân, trong đó, cả đô thành và lân ấp đều được nhờ cậy”.\r\nHiện nay, ngôi đền là một kiến trúc đẹp quay về hướng nam, gồm có cổng tam quan, phương đình, đại bái, thiêu hương, cung cấm và nhà hội đồng ở phía sau.\r\n\r\nTam quan có mặt bằng hình chữ nhật, chia thành năm gian với bốn bộ vì giống nhau kiểu “chồng rường giá chiêng hạ bảy”, một số thanh rường chạm văn hình học, mặt trước, gian giữa mở lối vào đền bằng cánh cử', 'denbachma.jpg', 2),
(44, 'Chùa Tây Phương', 'Nằm trên núi Câu Lậu - Thạch Xá, Thạch Thất, Hà Nội, chùa Tây Phương là nơi bảo lưu và gìn giữ rất nhiều pho tượng phật có giá trị. Nó thể hiện tinh hoa tuyệt vời của nghệ thuật tạc tượng Việt Nam và là minh chứng của một nền văn hoá có từ lâu đời. Đến với Chùa Tây Phương là đến với thế giới cực lạc, cõi niết bàn, chốn tu luyện của thần tiên.\r\n\r\nChùa Tây Phương được xây dựng khoảng thể kỷ 8 và là chùa cổ thứ hai sau chùa Dâu ở Bắc Ninh nước ta. Đường lên chùa Tây Phương phải trải qua hơn hai trăm bậc đá thang bằng đá ong.\r\n\r\nChùa được xây dựng theo lối kiến trúc kiểu chữ Tam với ba toà cấp dọc theo sườn núi, dựa vào thế núi từ thấp lên cao thành ba ngôi chùa song song với nhau gồm có chùa: Chùa Hạ, chùa Trung, Chùa Thượng. Mỗi toà đều có kiến trúc riêng rẽ nhưng lại kết hợp thành một quần thể. Mái chùa rất đặc biệt có những góc đao cong vút lên, cấu tạo theo kiểu hai lớp, hình thành một không gian rộng và thoáng đãng. Nét đặc sắc nhất của chùa là hệ thống tượng pháp.\r\n\r\nTổng cộng trong chùa có khoảng 82 pho tượng gỗ theo kiểu tượng tròn được đánh giá vào loại bậc nhất về nghệ thuật tạc tượng cổ nước ta.\r\n\r\nPhía góc của chùa Hạ là 8 pho tượng Kim Cương đang trong tư thế tiến thoái của võ thuật với dung mạo bất phàm. Phía bên chùa Trung là pho tượng Di đà cao sừng sững. Phía bên chùa Thượng, đặt cao nhất là tượng Tam thế đặc tả rất tinh vi, bên dưới là tượng Đức Thế Tôn giáng sinh, hai bên là bốn tôn giả: Mục Kiền Liên, Xá Lợi Thất, Đại Cao Diếp, A Lan Đà.\r\n\r\nĐặc biệt hơn cả là 18 pho tượng La Hán lớn bằng người thật trong các tư thế khác nhau ở hai bên tường lâu của thượng điện. Giống như nhà thơ Huy Cận miêu tả, mỗi vị là một nỗi khổ, cử chỉ, dáng điệu riêng thể hiện những tính cách khác nhau khá sinh động và hiếm thấy trong nghệ thuật điêu khắc cổ nước ta.\r\n\r\nẤn tượng không ai có thể quên là bức tượng tạc một người có tuổi, thân mình gầy gò, má hóp mắt sâu rất khác thường ngồi trong tư thế một chân xếp bằng một chân co, một tay đặt trên đầu, còn tay kia đặt vào lòn', 'tayphuong.jpg', 2),
(45, 'Yên Tử', 'Yên Tử là một ngọn núi đẹp nổi tiếng ở nước ta với những con suối trong vắt, lững lờ như vải lụa uốn quanh những rừng trúc, rừng tùng xanh bạt ngàn, thấp thoáng ẩn hiện là những ngọn tháp và đền chùa cổ kính. Nơi đây xưa kia làkinh đô Phật giáo với phái thiền Trúc Lâm nổi tiếng mà người sáng lập là một anh hùng dân tộc - vua Trần Nhân Tông (1258-1308).\r\n\r\nYên Tử là ngọn núi cao nhất nằm ở phía Tây Bắc thị xã Uông Bí thuộc tỉnh Quảng Ninh, cách trung tâm thị xã 17 km. Đứng ở độ cao 1068m, trên đỉnh núi, ta có thể bao quát cả vùng Đông Bắc rộng lớn với những đảo nhỏ thấp thoáng trong Vịnh Ha. Long như một bức tranh, xa xa là dòng xông Bạch Đằng cuộn sóng. Dọc con đường hành hương, đến chân núi là suối Giải Oan ngoằn ngoèo lượn khúc, nước trong vắt chảy róc rách qua những viên đá bóng nhẵn.\r\n\r\nTục truyền xưa kia vua Trần Nhân Tông nhường ngôi lại cho con trai là Trần Anh Tông rồi tìm đến cõi Phật. Vua Anh Tông có rất nhiều cung tần và mỹ nữ. Họ đã khuyên ông trở về cung gấm, nhưng không được nên họ lao mình xuống suối tựï vận . Vua Nhân Tông thương cảm cho họ nên lập một ngôi chùa siêu độ để giải oan, từ đó chuà và con suối mang tên là ÀGiãy Oan Cốc. Chuà được trùng tu nhiều lần, ẩn mình trong nhữnglùm cây soi bóng xuống suối trong uốn quanh trước mặt .\r\n\r\nTừ Giãy Oan Cốc leo ngược mỗi lúc một cao khó đi. Ven đường là hàng tùng cổ khoảng 7 đến 800 tuổi, thân rất to gắn chắc, rễ bò lan mặt đường như những con trăn lớn đang trườn mình thành những bậc thang vững chắc để đi . Đến dốc Voi phục, tục truyền xưa kia vua Trần Anh Tông lên thăm chùa Hoa Yên - nơi tu hành của Trần Nhân Tông, đều phải xuống kiệu leo bộ lên chùa. Bên cạnh dốc Voi phục là Hòn Ngọc, trên đỉnh có nhiều tháp và mộ, vôi lỡ gạch rêụ Đó là nơi yên nghĩ vĩnh hằng của các vị sư trụ trì chùa Yên Tử.\r\n\r\nQua Hòng Ngọc đến cụm tháp Huê. Quang, có tháp Huê. Quang là tháp của Ngựï Giác hoàng Trúc Lâm - Trần Nhân Tông. Tháp có 6 tầng, cao 10m làm bằng đá. Tầng thứ 2 của tháp đặt tượng thờ Trần Nhân Tông được coi l', 'yentu.jpg', 2);

